	[610] = {id = 610, type = 6, group_id = 0, first_title = "时空段位赛", second_title = "", content = "参加将获得<color='#FFFF00'>星辰积分</color>，每周二、四、六晚9：30", btn_type = 2, panel_id = "13500|1", npc_id = "", title_icon = "41210", lev = 40},
	[620] = {id = 620, type = 6, group_id = 0, first_title = "勇士战场", second_title = "", content = "参加将获得<color='#FFFF00'>星辰积分</color>，每周一、三、五晚8：00", btn_type = 2, panel_id = "12002", npc_id = "", title_icon = "41728", lev = 40},
