DataFestival = DataFestival or {}

DataFestival.data_festival_length = 29
DataFestival.data_festival = {
	["1_1"] = {mount = 1, day = 1, name = "元旦", desc = "今天是<color='#ffff00'>1月1日元旦</color>，公元前5000年前，古埃及人耕种时发现尼罗河泛滥的间隔时间大约365天，而当尼罗河初涨时，也正是太阳与天狼星同时从地平线上升起的时候。于是，古埃及人便把这一天定为一年的开始。", msg = "<color='#ffff00'>元旦节</color>快乐~新年新气象{face_1,29}"},
	["1_17"] = {mount = 1, day = 17, name = "小年", desc = "<color='#ffff00'>小年</color>是大家开始准备年货的日子。表示新年要有新气象，表达了中国劳动人民一种辞旧迎新、迎祥纳福的美好愿望。小年期间主要的民俗活动有贴春联，扫尘，祭灶等。", msg = "<color='#ffff00'>小年</color>快乐~记得要吃灶糖哦{face_1,18}"},
	["1_24"] = {mount = 1, day = 24, name = "除夕", desc = "<color='#ffff00'>除夕</color>，又称大年夜、除夕夜、除夜、岁除等。是时值每年农历腊月（十二月）的最后一个晚上。除，即去除的之意；夕，指夜晚。除夕也就是辞旧迎新、一元复始、万象更新的节日。", msg = "<color='#ffff00'>除夕</color>快乐~岁岁平安{face_1,18}"},
	["1_25"] = {mount = 1, day = 25, name = "春节", desc = "<color='#ffff00'>春节</color>是指汉字文化圈传统上的农历新年，俗称“年节”，传统名称为新年、大年、新岁，但口头上又称度岁、庆新岁、过年。人们在春节这一天都尽可能地回到家里和亲人团聚，表达对未来一年的热切期盼和对新一年生活的美好祝福。", msg = "<color='#ffff00'>春节</color>快乐~大吉大利{face_1,18}"},
	["2_14"] = {mount = 2, day = 14, name = "情人节", desc = "<color='#ffff00'>情人节</color>是一个关于爱、浪漫以及花、巧克力、贺卡的节日。男女在这一天互送礼物用以表达爱意或友好。情人节的晚餐约会充满着温馨与浪漫。", msg = "<color='#ffff00'>情人节</color>快乐~甜甜蜜蜜哦{face_1,18}"},
	["2_8"] = {mount = 2, day = 8, name = "元宵节", desc = "<color='#ffff00'>元宵节</color>，又称上元节、小正月、元夕或灯节，是春节之后的第一个重要节日，是中国亦是汉字文化圈的地区和海外华人的传统节日之一。正月是农历的元月，古人称夜为“宵”，所以把一年中第一个月圆之夜正月十五称为元宵节。", msg = "<color='#ffff00'>元宵</color>快乐~团团圆圆{face_1,18}"},
	["3_8"] = {mount = 3, day = 8, name = "妇女节", desc = "3月8日是国际劳动妇女节，又称三八节、<color='#ffff00'>妇女节</color>、三八国际妇女节,是世界各国妇女争取和平、平等、发展的节日。", msg = "<color='#ffff00'>妇女节</color>快乐~{face_1,18}"},
	["3_12"] = {mount = 3, day = 12, name = "植树节", desc = "<color='#ffff00'>植树节</color>是一些国家以法律规定宣传保护树木，并动员群众参加以植树造林为活动内容的节日。按时间长短可分为植树日，植树周或植树月，总称国际植树节。通过这种活动，激发人们爱林，造林的热情。", msg = "爱护植被，从我做起~{face_1,18}"},
	["3_15"] = {mount = 3, day = 15, name = "消费者权益日", desc = "每年的3月15日是“<color='#ffff00'>国际消费者权益日</color>” (World Consumer Rights Day) ，由国际消费者联盟组织于1983年确定，目的在于扩大消费者权益保护的宣传，使之在世界范围内得到重视，以促进各国和地区消费者组织之间的合作与交往，在国际范围内更好地保护消费者权益。", msg = "诚信经营，合理维权~{face_1,18}"},
	["4_1"] = {mount = 4, day = 1, name = "愚人节", desc = "今天是<color='#ffff00'>4月1日愚人节</color>。其实愚人节未被任何国家认定为法定节日。人们喜欢在这一天以各种方式互相欺骗和捉弄。但是请小心玩笑不要太过了，伤了感情，甚至是引起恐慌可不是好事。", msg = "<color='#ffff00'>愚人节</color>快乐~别被人骗咯{face_1,10}"},
	["4_4"] = {mount = 4, day = 4, name = "清明节", desc = "今天是<color='#ffff00'>清明节</color>。清明祭祖的习俗是与寒食节相关而来。而唐代开始，人们在清明扫墓时，伴之以踏青游乐的活动。并在宋元时期形成一个以祭祖扫墓为中心，将寒食风俗与上巳踏青等活动相融合的传统节日。", msg = "让我们在<color='#ffff00'>清明节</color>缅怀先人{face_1,21}，逝去的不会再回来，所以让我们好好珍惜现在吧"},
	["4_22"] = {mount = 4, day = 22, name = "地球日", desc = "今天是<color='#ffff00'>4月22日地球日</color>。地球及其生态系统是人类的家园，人类今后和未来要在经济、社会和环境三方面的需求之间实现平衡，必须与自然界和地球和谐共处。人类不拥有地球，而是属于地球。请珍爱我们所在的星球", msg = "大家不要只在<color='#ffff00'>地球日</color>才想起好好对待我们的地球呀{face_1,10}"},
	["5_1"] = {mount = 5, day = 1, name = "劳动节", desc = "今天是<color='#ffff00'>5月1日劳动节</color>。又称“国际示威游行日”。1886年5月1日，约35万人在美国参加了大规模罢工和示威游行。为纪念这次伟大的工人运动，第二国际成立大会时恩格斯宣布将每年的五月一日定为国际劳动节", msg = "<color='#ffff00'>劳动节</color>快乐~劳动最光荣！{face_1,10}"},
	["5_4"] = {mount = 5, day = 4, name = "青年节", desc = "今天是<color='#ffff00'>5月4日青年节</color>。五四运动是1919年5月4日发生在北京以青年学生为主的一场学生运动，广大群众广泛参与游行、请愿、罢工、暴力对抗政府等多形式的爱国运动。为了纪念此次运动，设立该节日。", msg = "<color='#ffff00'>青年节</color>快乐~不管你的年龄是否还年轻，但只要你有年轻的心，我们一样一起快乐过节{face_1,10}"},
	["5_10"] = {mount = 5, day = 10, name = "母亲节", desc = "今天是<color='#ffff00'>母亲节</color>。母亲节最早出现在古希腊；而现代的母亲节起源于美国，是每年5月的第二个星期日。母亲们在这一天通常会收到礼物，赠送母亲康乃馨，而中国的母亲花是萱草花，又叫忘忧草。请记得祝福一下母亲哦", msg = "<color='#ffff00'>母亲节</color>快乐~为自己亲爱的母亲送上一份真挚的祝福吧{face_1,10}"},
	["6_1"] = {mount = 6, day = 1, name = "儿童节", desc = "今天是<color='#ffff00'>6月1日儿童节</color>。国际儿童节的设立，是为了悼念德国法西斯枪杀了捷克利迪策村全部婴儿这期惨案和所有死难儿童，反对虐杀和毒害儿童，以及保障儿童权利。", msg = "<color='#ffff00'>儿童节</color>快乐~愿天下所有的孩子都能健康快乐的成长{face_1,38}"},
	["6_25"] = {mount = 6, day = 25, name = "端午节", desc = "今天是<color='#ffff00'>端午节</color>。端午节最初为祛病防疫的节日，吴越之地春秋之前有在农历五月初五以龙舟竞渡形式举行部落图腾祭祀的习俗；后因诗人屈原抱石自投汨罗江身死，又成为华人纪念屈原的传统节日。", msg = "<color='#ffff00'>端午节</color>快乐~一起去吃粽子划龙舟吧~{face_1,18}"},
	["6_21"] = {mount = 6, day = 21, name = "父亲节", desc = "今天是<color='#ffff00'>父亲节</color>。当母亲含辛茹苦地照顾我们时，父亲也在努力地扮演着上苍所赋予他的负重角色，当我们努力思考着该为父亲买什么礼物时，不妨反省一下我们是否爱我们的父亲？像他一样曾为我们无私地付出呢。", msg = "<color='#ffff00'>父亲节</color>快乐~为自己亲爱的父亲送上一份真挚的祝福吧{face_1,3}"},
	["7_1"] = {mount = 7, day = 1, name = "建党节", desc = "今天是<color='#ffff00'>7月1日建党节</color>。穿越血与火的历史烟云，历经建设与改革的风雨洗礼，伟大的中国共产党再次迎来自己的生日。我们的党正越来越健康成熟，会让我们国家越来越繁荣富强。", msg = "<color='#ffff00'>建党节</color>快乐~为带领我们走出黑暗走向光明的伟大党献上自己的祝福吧！{face_1,6}"},
	["8_1"] = {mount = 8, day = 1, name = "建军节", desc = "今天是<color='#ffff00'>8月1日建军节</color>。1933年7月11日，中华苏维埃共和国临时中央政府决定将8月1日作为中国工农红军成立纪念日。从此，每年8月1日就成为中国工农红军和后来中国人民解放军的建军节。", msg = "<color='#ffff00'>建军节</color>快乐~军队守护我们国门，保卫我们安宁，让我们向军人致敬！{face_1,25}"},
	["8_25"] = {mount = 8, day = 25, name = "七夕节", desc = "今天是<color='#ffff00'>七夕节</color>。七夕原本是妇女在农历七月七当天向织女星乞求智巧，故称“乞巧节”。后人们赋予了牛郎织女的传说，使得该节日变成了一个浪漫的日子。近代受国外文化影响，逐渐演变成中国的情人节。", msg = "<color='#ffff00'>七夕节</color>快乐~牛郎和织女今天会在鹊桥相会呢，如果你还单身，今天就去表白吧{face_1,34}"},
	["9_10"] = {mount = 9, day = 10, name = "教师节", desc = "今天是<color='#ffff00'>9月10日教师节</color>。师者，传道授业解惑者也。1985年国务院决议通过了9月10日为我们国家的教师节。今几年不少人提出以孔子诞生日9月28日作为教师节，对此教师们表示，心意到即可，不在乎具体的日子。", msg = "<color='#ffff00'>教师节</color>快乐~我的第二母亲,老师你辛苦了!教师节快乐！{face_1,3}"},
	["10_1"] = {mount = 10, day = 1, name = "国庆节", desc = "今天是<color='#ffff00'>10月1日国庆节</color>。1949年12月2日，中央人民政府通过规定每年10月1日为国庆日，并以这一天作为宣告中华人民共和国成立的日子。从1950年起，每年的10月1日成为了中国各族人民隆重欢庆的节日。", msg = "<color='#ffff00'>国庆节</color>快乐~希望我们的国家永远繁荣昌盛{face_1,18}"},
	["10_25"] = {mount = 10, day = 25, name = "重阳节", desc = "今天是<color='#ffff00'>重阳节</color>。重阳节一般会进行出游赏秋、登高远眺、观赏菊花、遍插茱萸、吃重阳糕、饮菊花酒等活动。每年的农历九月初九日，与除夕、清明节、中元节三节统称中国传统四大祭祖的节日", msg = "<color='#ffff00'>重阳节</color>快乐~一起去登高吧{face_1,26}"},
	["10_31"] = {mount = 10, day = 31, name = "万圣节", desc = "今天是<color='#ffff00'>10月31日万圣节</color>。万圣节是西方国家的传统节日。以前人们认为万圣的夜晚到处有女巫和鬼魂，所以把万圣夜看作尽情玩闹、讲鬼故事和互相吓唬的好机会。如今人们已经习惯于把它变成一场真正的“狂欢”。", msg = "<color='#ffff00'>Trick or treat</color>，交出糖果吧，不然就捣乱{face_1,22}"},
	["11_26"] = {mount = 11, day = 26, name = "感恩节", desc = "今天是<color='#ffff00'>感恩节</color>。1941年，美国正式将每年11月第四个星期四定为“感恩节”，后面这一节日逐渐为世界各地人民所接受，在感恩节这一天里，对曾经帮助过我们的人致以诚挚的感谢吧！", msg = "Happy<color='#ffff00'>Thanksgiving</color>Day，今天火鸡又要被烤了{face_1,15}"},
	["12_21"] = {mount = 12, day = 21, name = "冬至", desc = "今天是<color='#ffff00'>冬至节</color>。冬节大如年。冬至不仅是二十四节气之一，在古代也是很重要的节日，和春节并列。冬至是养生的大好时机，北方这一天通常会吃饺子，而南方会吃汤圆。", msg = "<color='#ffff00'>冬至节</color>快乐~今天有吃饺子/汤圆吗{face_1,10}"},
	["12_25"] = {mount = 12, day = 25, name = "圣诞节", desc = "今天是<color='#ffff00'>12月25日圣诞节</color>。圣诞节又名耶诞节，是为了纪念耶稣诞生的日子。而著名的圣诞老人相传是一名叫尼古拉斯的人，散尽家财为救济贫困百姓，人们为了怀念他而塑造出的形象。", msg = "<color='#ffff00'>圣诞节</color>快乐~圣诞老人今晚上可能会去你家哦~{face_1,29}"},
	["0_0"] = {mount = 0, day = 0, name = "周三要加油", desc = "今天是<color='#ffff00'>星期三</color>。一周又过去一半了呢~离周末越来越近了，加油{face_1, 18}", msg = "<color='#ffff00'>星期三</color>来啦~一周又过去一半了呢~离周末越来越近了，加油{face_1, 18}"}
}
