	{camp_id = 1110, theme = "美美自拍", npc_name = "星辰小美", desc = "晒出自拍照即可参与话题{face_1,1};登上首页将受到万人追捧哟{face_1,38};人气高涨还有惊喜好礼{face_1,10};照片配合简介或口号更佳{face_1,25}", join_btn = "我要参与", desc2 = "<color='#ffff00'>本期话题#美美自拍#</color>晒出你的自拍照，无论是萌萌哒、帅帅的、美美的、中二的、霸气的、搞怪的…自拍照，都可以分享出来哟，图片配上自我介绍或口号，准备登上首页吧！", topic_btn = "美美自拍", content = "#美美自拍#晒出你的自拍照，无论是萌萌哒、帅帅的、美美的、中二的、霸气的、搞怪的…自拍照，都可以分享出来哟，图片配上自我介绍或口号，看看谁才是星辰SuperStar{face_1,25}", picture = 1001},
	{camp_id = 1115, theme = "浓情端午", npc_name = "星辰小美", desc = "分享独特的端午习俗{face_1,9};甜粽子好吃还是咸的好吃{face_1,54};展示你与亲朋好友共度佳节{face_1,38};图文并茂更容易登上首页{face_1,56}", join_btn = "我要参与", desc2 = "<color='#ffff00'>本期话题#浓情端午#</color>端午佳节，祖国地域辽阔，各地端午习俗不尽相同，分享你的端午所见所闻，亦或是和亲朋好友共度佳节，图片配上简单说明，更容易登上首页哟", topic_btn = "浓情端午", content = "#浓情端午#端午佳节，祖国地域辽阔，各地端午习俗不尽相同，分享你的端午所见所闻，亦或是和亲朋好友共度佳节的情景，图片配上简单说明，一起享受美好时光吧{face_1,38}", picture = 1002},
	{camp_id = 1116, theme = "说走就走", npc_name = "星辰小美", desc = "盛夏是出行好季节，准备出发{face_1,1};来一场说走就走的旅行吧{face_1,25};分享你在旅途中拍摄的风景{face_1,38};或者各种有趣的经历见闻{face_1,56};图文并茂才会人气高涨哟{face_1,29}", join_btn = "我要参与", desc2 = "<color='#ffff00'>本期话题#说走就走#</color>盛夏已至，邀约亲朋好友，来一场说走就走的旅行吧！美如画的风景、有趣的人和事、难忘的旅途…分享你曾经在旅途中的所见所闻吧，图片配上简单说明，更容易登上首页哟！", topic_btn = "说走就走", content = "#说走就走#盛夏已至，邀约亲朋好友，来一场说走就走的旅行吧！美如画的风景、有趣的人和事、难忘的旅途…图片配上文字说明，分享你曾经在旅途中的所见所闻吧{face_1,56}", picture = 1003},
	{camp_id = 1139, theme = "青葱校园", npc_name = "星辰小美", desc = "美好的校园时光总是令人向往{face_1,56};奇葩的舍友有趣的老师{face_1,52};热泪盈眶的军训或毕业典礼{face_1,8};用照片记录你最难忘的瞬间{face_1,3};分享你的青葱校园生活吧{face_1,1};图文并茂才会引起共鸣哟{face_1,38}", join_btn = "我要参与", desc2 = "<color='#ffff00'>本期话题#青葱校园#</color>美好的校园时光总是令人向往，相遇同桌的你、球场挥汗如雨、军训英姿飒爽、毕业典礼依依不舍…用照片记录你最难忘的瞬间，分享你的青葱校园生活吧，图片配上说明介绍，才会更容易登上首页哟！", topic_btn = "青葱校园", content = "#青葱校园#美好的校园时光总是令人向往，相遇同桌的你、球场挥汗如雨、军训英姿飒爽、毕业典礼依依不舍…用照片记录你最难忘的瞬间，分享你的青葱校园生活吧，图片配上说明介绍，才会更容易登上首页哟{face_1,56}", picture = 1004},
	{camp_id = 1186, theme = "爱笑的你", npc_name = "星辰小美", desc = "和身边的人快乐过每一天{face_1,56};来跟大家分享你的欢乐秘笈吧！;有趣的笑话{face_1,51};搞怪的鬼脸{face_1,118};或灿烂的笑脸{face_1,52};图文并茂才会引起共鸣哟{face_1,38}", join_btn = "我要参与", desc2 = "<color='#ffff00'>本期话题#爱笑的你#</color>记忆里最深刻的总是你的灿烂笑脸，无论是小小的笑话，还是一个逗比的举动都能让大家乐开花…用照片记录你最难忘的瞬间，分享你的一些欢乐片刻或欢乐笑话吧，图片配上说明介绍，才会更容易登上首页哟{face_1,56}", topic_btn = "爱笑的你", content = "#爱笑的你#快乐是人们积极向上的源泉，愉快的分享也能影响周边的人变得快乐哟…用照片记录你最难忘的开心瞬间：微笑或开怀大笑的自拍；有趣的新段子；搞笑的图片…把快乐分享给更多的小伙伴吧，图片配上说明介绍，才会更容易登上首页哟{face_1,56}", picture = 1005},
	{camp_id = 1421, theme = "你我的春节", npc_name = "星辰小美", desc = "分享你的春节生活{face_1,56};分享家乡春节美食{face_1,51};拼手气抢红包看谁是欧皇{face_1,118};展示你与亲朋好友共度佳节{face_1,38};图文并茂更容易登上首页{face_1,56}", join_btn = "我要参与", desc2 = "<color='#ffff00'>本期话题#你我的春节#</color>春节是中国人最重视的节日，亲朋好友欢聚一堂，各种美食让你每逢佳节胖三斤，新春红包满天飞，新春祝福也响不停……快来与大家分享你的春节快乐生活吧，图片配上说明介绍，才会更容易登上首页哟{face_1,56}", topic_btn = "你我的春节", content = "#你我的春节#春节是中国人最重视的节日，亲朋好友欢聚一堂，各种美食让你每逢佳节胖三斤，新春红包满天飞，新春祝福也响不停……快来与大家分享你的春节快乐生活吧，图片配上说明介绍，才会更容易登上首页哟{face_1,56}", picture = 1006},
	{camp_id = 1633, theme = "你我的故事", npc_name = "星辰小美", desc = "分享你的关于感情的故事{face_1,9};分享游戏中关于友情爱情的故事;在游戏里遇见哪个TA让你印象深刻呢？;展示你与朋友或爱人的那点小事;图文并茂更容易登上首页{face_1,56}", join_btn = "我要参与", desc2 = "<color='#ffff00'>本期话题#你我的故事#</color>人的相遇相识是个缘分，在实现中会遇到各色各样的人，在游戏里也会遇见不同的可爱人儿，编写出精彩的故事……快来与大家分享你在游戏中遇到的友情或爱情故事吧，图片配上说明介绍，才会更容易登上首页哟{face_1,56}", topic_btn = "你我的故事", content = "#你我的故事#人的相遇相识是一种奇妙的缘分，在现实生活中会遇到各色各样的人，在游戏中也会遇到不同的可爱人儿，编写出独特且精彩的故事……快来与大家分享你在游戏中发生的爱情或友情故事吧，图片配上说明介绍，才会更容易登上首页哟{face_1,56}", picture = 1007},
	{camp_id = 1778, theme = "中秋乐事", npc_name = "星辰小美", desc = "分享各地方关于中秋的习俗{face_1,9};分享中秋节日里和家人的温馨时刻;在游戏里和TA度过了怎么样的中秋团圆夜;展示你与朋友或家人的那点温馨小事;图文并茂更容易登上首页{face_1,56}", join_btn = "我要参与", desc2 = "<color='#ffff00'>本期话题#中秋乐事#</color>中秋月圆之夜，正是团圆相聚的日子，和家人朋友吃吃月饼，共赏明月。可以跟大家一起来分享你最喜欢的月饼，也可以分享和家人发生的温馨故事，图片配上说明介绍，才会更容易登上首页哟{face_1,56}", topic_btn = "中秋乐事", content = "#中秋乐事#中秋月圆夜，如此良辰美景正是和亲朋好友相聚的日子，吃着美味的各式各样的月饼，观赏一轮明月，真是愉快至极……快来与大家分享你在中秋温馨乐事吧，图片配上说明介绍，才会更容易登上首页哟{face_1,56}", picture = 1008},
	{camp_id = 3028, theme = "你我的春节", npc_name = "星辰小美", desc = "分享你的春节生活{face_1,56};分享家乡春节美食{face_1,51};拼手气抢红包看谁是欧皇{face_1,118};展示你与亲朋好友共度佳节{face_1,38};图文并茂更容易登上首页{face_1,56}", join_btn = "我要参与", desc2 = "<color='#ffff00'>本期话题#你我的春节#</color>春节是中国人最重视的节日，亲朋好友欢聚一堂，各种美食让你每逢佳节胖三斤，新春红包满天飞，新春祝福也响不停……快来与大家分享你的春节快乐生活吧，图片配上说明介绍，才会更容易登上首页哟{face_1,56}", topic_btn = "你我的春节", content = "#你我的春节#春节是中国人最重视的节日，亲朋好友欢聚一堂，各种美食让你每逢佳节胖三斤，新春红包满天飞，新春祝福也响不停……快来与大家分享你的春节快乐生活吧，图片配上说明介绍，才会更容易登上首页哟{face_1,56}", picture = 1006}
}

