	[1110] = {camp_id = 1110, unit_id = 76299, npc_name = "星辰话题", npc_desc = "<color='#ffff00'>本期话题#美美自拍#</color>晒出你的自拍照，无论是萌萌哒、帅帅的、美美的、中二的、霸气的、搞怪的…自拍照，都可以分享出来哟，图片配上自我介绍或口号，看看谁才是星辰SuperStar{face_1,25}"},
	[1115] = {camp_id = 1115, unit_id = 76299, npc_name = "星辰话题", npc_desc = "<color='#ffff00'>本期话题#浓情端午#</color>端午佳节，祖国地域辽阔，各地端午习俗不尽相同，分享你的端午所见所闻，亦或是和亲朋好友共度佳节的情景，图片配上简单说明，一起享受美好时光吧{face_1,38}"},
	[1116] = {camp_id = 1116, unit_id = 76299, npc_name = "星辰话题", npc_desc = "<color='#ffff00'>本期话题#说走就走#</color>盛夏已至，邀约亲朋好友，来一场说走就走的旅行吧！美如画的风景、有趣的人和事、难忘的旅途…图片配上文字说明，分享你曾经在旅途中的所见所闻吧{face_1,56}"},
	[1139] = {camp_id = 1139, unit_id = 76299, npc_name = "星辰话题", npc_desc = "<color='#ffff00'>本期话题#青葱校园#</color>美好的校园时光总是令人向往，相遇同桌的你、球场挥汗如雨、军训英姿飒爽、毕业典礼依依不舍…用照片记录你最难忘的瞬间，分享你的青葱校园生活吧，图片配上说明介绍，才会更容易登上首页哟{face_1,56}"},
	[1186] = {camp_id = 1186, unit_id = 76299, npc_name = "星辰话题", npc_desc = "<color='#ffff00'>本期话题#爱笑的你#</color>快乐是人们积极向上的源泉，愉快的分享也能影响周边的人变得快乐哟…用照片记录你最难忘的开心瞬间：微笑或开怀大笑的自拍；有趣的新段子；搞笑的图片…把快乐分享给更多的小伙伴吧，图片配上说明介绍，才会更容易登上首页哟{face_1,56}"},
	[1421] = {camp_id = 1421, unit_id = 76299, npc_name = "星辰话题", npc_desc = "<color='#ffff00'>本期话题#你我的春节#</color>春节是中国人最重视的节日，亲朋好友欢聚一堂，各种美食让你每逢佳节胖三斤，新春红包满天飞，新春祝福也响不停……快来与大家分享你的春节快乐生活吧，图片配上说明介绍，才会更容易登上首页哟{face_1,56}"},
