	[10380]=[[{10380,{{label = 405,val = "10171,40000,伊芙,{point,10003,1770,1789},7,stand,1,[]"},{label = 405,val = "10173,20035,神秘人,{point,10003,1940,1920},2,stand,1,[]"},{label = 405,val = "10172,40013,蓝雯,{point,10003,1955,1767},0,stand,1,[]"},{label = 101,val = "500,1900,1770"},{label = 600,val = "800"},{label = 402,val = "0,10173,可恶,900"},{label = 402,val = "0,10173,若不是这只是我投影……,1100"},{label = 600,val = "600"},{label = 221,val = "1007"},{label = 600,val = "1000"},{label = 214,val = "0,10174,dead,1000"},{label = 600,val = "500"},{label = 402,val = "0,10174,{46},600"},{label = 402,val = "0,10172,原来他被魔物附身了啊！,1100"},{label = 402,val = "0,10171,必须立刻告诉<color='#66ff00'>德林老师</color>。{25},1300"},{label = 100,val = "500"},{label = 408,val = "10171"},{label = 408,val = "10172"},{label = 408,val = "10174"}}}]],
	[10384]=[[{10384,{{label = 401,val = "2,德林导师,20001,对了，正好我这里有一只学院费好大力气捕获的<color='#66ff00'>宠物花莹</color>，就送给你吧。"}}}]],
	[10386]=[[{10386,{{label = 401,val = "2,花莹,30003,花莹有主人啦~哇，主人，你可真高{3}{3}不过没关系，花莹可以飞呢，你看你看{38}{38}"},{label = 401,val = "2,德林导师,20001,宠物最重要的是三项：<color='#66ff00'>资质</color>、<color='#66ff00'>成长与技能</color>，<color='#ffff00'>资质</color>越高越好、<color='#ffff00'>成长</color>则分红橙紫蓝，红最高，蓝最低，<color='#ffff00'>技能</color>则是不同宠物带有不同技能。"},{label = 401,val = "2,德林导师,20001,若是对宠物不满意，则可通过洗髓改变<color='#66ff00'>资质</color>、<color='#66ff00'>成长与技能</color>，洗髓需消耗<color='#66ff00'>星辰精华</color>或者钻石，你现在试试看吧。"}}}]],
	[10390]=[[{10390,{{label = 101,val = "1000,2503,1590"},{label = 405,val = "10178,40013,蓝雯,{point,10001,2368,1705},5,stand,1,[]"},{label = 600,val = "800"},{label = 406,val = "10178,1000,2609,1586"},{label = 100,val = "1000"},{label = 402,val = "0,10178,嘿，<color='#66ff00'>[role]</color>,1100"},{label = 406,val = "0,500,2740,1521"},{label = 502,val = "1"},{label = 404,val = "0,0,蓝雯,900"},{label = 404,val = "0,0,你怎么来了？{35},1300"},{label = 402,val = "0,10178,你很强,900"},{label = 402,val = "0,10178,我看到了无穷的潜力{26},1300"},{label = 402,val = "0,10178,让我加入你的队伍吧{15},1300"},{label = 402,val = "0,10178,一起变强！{25},1300"},{label = 408,val = "10178"}}}]],
	[10400]=[[{10400,{{label = 104,val = "2,1000"},{label = 403,val = "2,0,0,这是怎么了？{35}"},{label = 104,val = "2,600"},{label = 401,val = "2,德林导师,20001,地震，这是地震！！！{23}{23}怎会突发地震，监察处毫无预警啊！{35}"},{label = 401,val = "2,德林导师,20001,这地震突发，学院怕是会有些乱，<color='#66ff00'>[role]</color>，肖恩教授肯定焦头烂额，你快去帮帮他。"}}}]],
	[10410]=[[{10410,{{label = 401,val = "2,肖恩教授,40040,真是一个积极的好学生，<color='#66ff00'>[role]</color>，正好林雷魔化这件事因为地震突发我暂时没时间调查，就交给你吧。{36}{36}{36}"},{label = 403,val = "2,0,0,没问题，不过，教授能指点下我该从何处下手么？"},{label = 401,val = "2,肖恩教授,40040,林雷魔化，关键是那枚藏有“影”的投影的戒指，这枚戒指是从交易行买的，你去问询一下，哎，希望交易商人葛朗台这老伙计别到老了还晚节不保……"}}}]],
	[10430]=[[{10430,{{label = 405,val = "10150,40046,杜维,{point,10001,2906,3770},4,stand,1,[]"},{label = 401,val = "2,杜维,40046,你说林雷的戒指？那戒指是我从码头上的水手长洛克那低价买来的，真的，不骗你！"},{label = 401,val = "2,杜维,40046,现在林雷之事全学院都知道了，我真要是有鬼，肯定早就逃了，哪会还呆在学院啊？"},{label = 403,val = "2,0,0,水手长洛克？不认识，不过他既然在码头混，波芘船长肯定认识！"},{label = 408,val = "10150"}}}]],
	[10434]=[[{10434,{{label = 408,val = "20112"},{label = 405,val = "10152,40080,水手长洛克,{point,10001,4303,1540},1,stand,1,[]"},{label = 401,val = "2,水手长洛克,40080,别、别打了别打了，痛死、死我了，您老随、随便问。{39}{39}"},{label = 403,val = "2,0,0,说吧，你卖给杜维的戒指是哪来的！"},{label = 401,val = "2,水手长洛克,40080,林雷的戒指？是地院的马、马丁导师无意间落、落在船上的，被我拾了拿去卖、卖了……{15}{15}"},{label = 403,val = "2,0,0,这……竟然牵扯到地院的导师，这事必须告诉副院长肖恩教授！"},{label = 408,val = "10152"}}}]],
	[10440]=[[{10440,{{label = 401,val = "2,傀儡守卫,40021,{10}{10}看来真与主人的预测一样，会有人偷入浮空岛，找死！"}}}]],
	[10450]=[[{10450,{{label = 401,val = "2,伊芙,40000,这种时候，马丁竟然不在学院内？此事必有蹊跷！{9}"}}}]],
	[10454]=[[{10454,{{label = 408,val = "20116"},{label = 405,val = "10153,40090,仆从多摩尔,{point,10001,3954,1922},7,stand,1,[]"},{label = 403,val = "2,0,0,现在可以说了吧，你的导师马丁呢？非要挨顿揍，太欠了！{32}{32}"},{label = 401,val = "2,仆从多摩尔,40090,我其实也不知道他去哪了，不过我最后一次见他是在波比船长那里。"},{label = 403,val = "2,0,0,波比船长？难道马丁要出航？"},{label = 408,val = "10153"}}}]],
	[10456]=[[{10456,{{label = 405,val = "10155,40100,海盗探子,{point,10002,1936,1717},2,stand,1,[]"},{label = 401,val = "2,海盗探子,40100,你要找的那人向北去了，你沿着路走就行{4}{4}……哎，竟然还有敢揍海盗的人，以后怎么混啊{8}{8}{8}"},{label = 403,val = "2,0,0,好了知道了，另外，出来混就要有被揍的觉悟。{10}"},{label = 408,val = "10155"}}}]],
	[10460]=[[{10460,{{label = 405,val = "10154,40043,凯恩,{point,10002,1826,1079},0,stand,1,[]"},{label = 401,val = "2,凯恩,40043,<color='#66ff00'>[role]</color>~伊芙~想不到竟然能在这碰见你们这俩孩子，这么急急忙忙的是做什么呐？{26}"},{label = 401,val = "2,伊芙,40000,原来是牛头人部族的凯恩酋长，真是好久不见呢~对了，酋长大叔有碰见过地院的马丁导师么？"},{label = 401,val = "2,凯恩,40043,马丁？嗯，刚才还与大叔我聊了两句，不过他有事就先走了，向西去了。"},{label = 401,val = "2,伊芙,40000,快追！"},{label = 401,val = "2,凯恩,40043,哎？你们找他干嘛？这俩心急的孩子，哎，我跟你们一起去，等等大叔我……"},{label = 408,val = "10154"}}}]],
	[10470]=[[{10470,{{label = 408,val = "20126"},{label = 405,val = "10160,40077, 马丁, {point, 10002, 1298, 492}, 7, stand, 1, []"},{label = 401,val = "2,马丁,40077,想不到追上来的人竟然是你。{25}"},{label = 403,val = "2,0,0,马丁老师，根据我们的调查，林雷的戒指最早是从您那流出的，希望您跟跟我回去见肖恩教授。"},{label = 401,val = "2,马丁,40077,不用回去见他了，我就是你们要找的人，不过，仅凭你们还抓不住我！给我拦下他们！{17}"},{label = 406,val = "10160,500,664,379"},{label = 408,val = "10160"}}}]],
	[10480]=[[{10480,{{label = 405,val = "10180,40043,凯恩,{point,10003,2473,2586},1,stand,1,[]"},{label = 405,val = "10190,40000,伊芙,{point,10003,2158,2776},5,stand,1,[]"},{label = 401,val = "2,巡游者,20013,既然你们亲眼见他进入，那估计他应该是隐身了，以往遇到隐身的目标，主要靠福波斯贤者帮助我解决，你们应该去向贤者寻求帮助。"},{label = 401,val = "2,伊芙,40000,飞瀑村很小，马丁潜行时移动缓慢，我们应该先封锁住这里，来个关门打狗！{32}"},{label = 214,val = "0,10190,1000,1000"},{label = 401,val = "2,凯恩,40043,这主意不错，伊芙你守住这里的出口，我去守住西北的出口，<color='#66ff00'>[role]</color>你去找找贤者福波斯，希望能对我们有所帮助。{25}"},{label = 403,val = "2,0,0,好，就这么办！"},{label = 221,val = "1008"},{label = 408,val = "10180"},{label = 408,val = "10190"}}}]],
	[10490]=[[{10490,{{label = 401,val = "2,贤者福波斯,20014,嗯，好了！现在西北风正盛，你把这显隐之尘交予酋长大叔，让他在高地挥洒，并以苍木叶扇动，就可以让显隐之尘飘散到整个飞瀑村了。"}}}]],
	[10494]=[[{10494,{{label = 401,val = "2,娜迦首领,40130,终于逮到你了！哼，竟敢从我们娜迦手上抢东西，找死！{13}"}}}]],
	[10496]=[[{10496,{{label = 403,val = "2,0,0,想不到这些娜迦竟然还敢来复仇……好了，现在应该抓紧时间把显隐之尘给凯恩大叔。"}}}]],
	[10500]=[[{10500,{{label = 405,val = "20151,40043,凯恩,{point,10003,754,405},7,stand,1,[]"},{label = 403,val = "2,0,0,凯恩大叔，这是贤者赠予的显隐之尘，可破除隐身魔法，麻烦大叔用苍木叶扇动将它抛洒出去，顺着西北风让它飘散到飞瀑村每个角落。"},{label = 401,val = "2,凯恩,40043,放心，交给大叔我吧！你先回贤者那里去，监视村中各处以免马丁出现时鞭长莫及。"},{label = 403,val = "2,0,0,恩恩，那一切就交给大叔了，加油！{18}"},{label = 408,val = "20151"}}}]],
	[10502]=[[{10502,{{label = 403,val = "2,0,0,对了，飞瀑村这么大，要想将所有情况收入眼中，恐怕不容易吧？{24}"},{label = 401,val = "2,贤者福波斯,20014,我倒是有个办法，如果你能搞到一副望远镜的话，倒是很容易了解到飞瀑村发生的任何情况"},{label = 403,val = "2,0,0,望远镜？对了，我记得追踪马丁时在月痕海岸遇到过几个海盗探子，他们手上就有望远镜。{38}"}}}]],
	[10504]=[[{10504,{{label = 403,val = "2,0,0,那谁，你手上的望远镜借我用用，放心，用完就还你。{17}"},{label = 401,val = "2,探子统领,40140,有没搞错？不久前才揍了我手下一顿，现在又来抢我的望远镜，海盗这工作到底还能不能混下去啊！！！{12}"},{label = 403,val = "2,0,0,自己找打，不怪我哦~"}}}]],
	[10510]=[[{10510,{{label = 403,val = "2,0,0,你们是谁？为何拦住我们？"},{label = 401,val = "2,神秘劫囚者,40150,我们是谁你不用管，你只需要知道，我们，只要马丁！！！"},{label = 403,val = "2,0,0,那就来战！"}}}]],
	[10512]=[[{10512,{{label = 403,val = "2,0,0,竟然有人劫囚，真是……必须得找个地方休整下，接下来估计还有战斗，恩，就去妖姬小姐那好了！"}}}]],
	[10514]=[[{10514,{{label = 403,val = "2,0,0,我就知道，不会只有一波试图劫走马丁的人。"},{label = 401,val = "2,劫囚者兽灵,40160,看来你挺有觉悟的嘛，哼，劝你还是放走马丁，否则……{13}"},{label = 403,val = "2,0,0,废话那么多干嘛，赶紧来战，我赶时间！"}}}]],
	[10520]=[[{10520,{{label = 401,val = "2,肖恩教授,20019,[role]，还记得马丁么？自从你抓捕他之后，学院试图从他口中挖出他背后组织的消息，但很遗憾，他的意志太坚韧了。"},{label = 403,val = "2,0,0,教授，有什么需要我效劳的，请尽管吩咐吧。"},{label = 401,val = "2,肖恩教授,20019,很好！我们需要用以<color='#66ff00'>「魔」</color>~为名的梦魇之王的力量来击破他的心防，魔曾欠我一个人情。"},{label = 403,val = "2,0,0,那我要如何才能找到梦魇之王呢？"},{label = 401,val = "2,肖恩教授,20019,精灵之森的大长老知道梦魇之王的下落，不过精灵之森很难进入，月痕海岸的旅人可能知道进入的办法，你去找他试试吧。"}}}]],
	[10530]=[[{10530,{{label = 401,val = "2,大长老,20027,你们竟然想找梦魇之王？看你年纪，也不像是活得不耐烦了的人啊……{24}"},{label = 403,val = "2,0,0,我们需要他的帮助，我会尽全力“拜托”梦魇之王陛下的。"},{label = 401,val = "2,大长老,20027,他的帮助？果然，活得越久，就能见到越多的怪事。{26}"},{label = 401,val = "2,大长老,20027,好吧，年轻人，过了前面的云桥，喝掉树上流下的沉眠泉水，就能引来梦魇之王分身，干掉他，你就能引起本体注意了。"}}}]],
	[10550]=[[{10550,{{label = 403,val = "2,0,0,{23}{23}{23}这泉水……竟然是用桶装的……"},{label = 213,val = "dead,1000"},{label = 201,val = "10029"},{label = 600,val = "100"},{label = 203,val = "16122,1338,674"},{label = 401,val = "2,魔的分身,40058,凡人，你胆敢进入梦魇之王的领地？"},{label = 213,val = "standup,1000"},{label = 403,val = "2,0,0,抱歉，我只是想拜见梦境王国的主宰。"}}}]],
	[10560]=[[{10560,{{label = 405,val = "10200,40058, 魔, {point, 10004,1549, 2271}, 6, stand, 1, []},"},{label = 401,val = "2,魔,40058,{12}{12}{12}是谁？敢在我的国度撒野！"},{label = 502,val = "2"},{label = 403,val = "2,0,0,陛下，我是来自星辰学园的[role]，很抱歉击碎了您的分身，但我只是想拜见您并带来肖恩教授的问候。"},{label = 401,val = "2,魔,40058,啧啧……竟然是老债主的小崽子{5}{5}，好吧，看在肖恩的份上，就绕你这一次。"},{label = 403,val = "2,0,0,感谢陛下的宽容，另外，这次来，其实是想请陛下帮助我们击破一个被毁灭军团魔染的内奸的意志……"},{label = 401,val = "2,魔,40058,毁灭军团？呵，离五百年一次的大战还有不到10年，看来他们也已经按讷不住了！好吧，我就帮你们这一回。{25}{25}{25}"}}}]],
	[10570]=[[{10570,{{label = 406,val = "10220,1000,2785,1497"},{label = 401,val = "2,肖恩教授,20019,老朋友，又见面了，很高兴你能来。"},{label = 401,val = "2,魔,40058,但我并不想见到你{30}{30}{30}……好了，开始吧，我会让这个竟敢投靠毁灭军团的家伙进入沉睡，并将其意志实体化。"},{label = 401,val = "2,肖恩教授,20019,那就麻烦你了，这次之后，咱们两不相欠。"},{label = 401,val = "2,魔,40058,不用，毁灭军团的事就是整个绯月世界的事，我照样还欠你一次，好了，别废话了。{25}"},{label = 406,val = "10220,1000,3017,1581"},{label = 215,val = "0,10220,5"},{label = 214,val = "0,10220,1000,1000"}}}]],
	[10590]=[[{10590,{{label = 401,val = "2,魔,40058,好了，干掉他那被我具象化的意志，你们就能从他口中得到想要的一切了，那么，我就先离开了，以后，关于毁灭军团的一切，都可以找我。"}}}]],
	[10600]=[[{10600,{{label = 401,val = "2,肖恩教授,20019,好了，这下可以好好审问一下他了，<color='#66ff00'>[role]</color>这次真是辛苦你了。"}}}]],
	[10610]=[[{10610,{{label = 401,val = "2,肖恩教授,20019,<color='#66ff00'>[role]</color>，你来的正好，我知道了马丁与他的联络人之间的联系方式，你去码头上找一个奇怪的箱子，把它带给我。"}}}]],
	[10614]=[[{10614,{{label = 401,val = "2,魔像傀儡,40190,发现气息不符，防御反击模式开启。"},{label = 403,val = "2,0,0,糟糕，马丁的联络人竟然为这箱子配置了魔像傀儡守护！必须尽快干掉它们，否则就暴露了。"}}}]],
	[10616]=[[{10616,{{label = 403,val = "2,0,0,咦，为什么箱子中的信居然是空白的？"},{label = 401,val = "2,妖姬小姐,20009,笨呐，很明显这是密信，你去找高级炼金术师试试，他们那种人最擅长这个。"}}}]],
	[10618]=[[{10618,{{label = 401,val = "2,贤者福波斯,20014,据说北方的古宅中存有上古文字的字典，你可以去那看看，若真有，便拿回去让肖恩翻译吧，这种秘密，越少人知道越好。"}}}]],
	[10620]=[[{10620,{{label = 401,val = "2,肖恩教授,20019,我从箱子中发现马丁的上线正在召集人手，据说有货通过辖区，另外，他们背后的组织是<color='#66ff00'>「摘星者」</color>！！！"},{label = 403,val = "2,0,0,<color='#66ff00'>「摘星者」</color>？"},{label = 401,val = "2,肖恩教授,20019,是的，那是魔域毁灭军团在我们世界的暗子，必对我们的威胁非常大……"},{label = 403,val = "2,0,0,或许，我们可以用马丁落网这个契机打入他们内部，马丁的手下在劫囚过程中已经被我们清干净了，而且，马丁手下也不知道马丁真实身份……"},{label = 401,val = "2,肖恩教授,20019,嗯，这个想法不错，不过马丁身份有风险，最好是制服他上线后用他上线的身份，你去找魔药师安妮，她会帮你的。"}}}]],
	[10625]=[[{10625,{{label = 401,val = "2,肖恩教授,20019,记住，马丁上线的密探代号是<color='#66ff00'>「鹰」</color>，而马丁是<color='#66ff00'>「鱼」</color>，暗号则是<color='#66ff00'>「落日」「残阳」</color>"}}}]],
	[10630]=[[{10630,{{label = 213,val = "attack,1000"},{label = 403,val = "2,0,0,落日。"},{label = 401,val = "2,神秘人,40240,残阳！<color='#66ff00'>「鱼」</color>，你你这次过来不要紧？"},{label = 403,val = "2,0,0,没事的，<color='#66ff00'>「鹰」</color>，没有任何人注意到我，货怎么样了？"},{label = 401,val = "2,神秘人,40240,<color='#66ff00'>「女妖」</color>正在飞瀑村等货，不过，抱歉这次你不能参加，毕竟，你的身份太重要，你要监视星辰学园，以及，最重要的那个人，不能暴露。"},{label = 403,val = "2,0,0,好吧，看来，只能对不住你了。"}}}]],
	[10640]=[[{10640,{{label = 403,val = "2,0,0,好了，接下来，就该去飞瀑村找接头的<color='#66ff00'>「女妖」</color>了，真是刺激，不过得先去找贤者福波斯问问有没有什么奇怪的人出现！"}}}]],
	[10650]=[[{10650,{{label = 403,val = "2,0,0,货怎么样了？"},{label = 401,val = "2,神秘人,40062,你是谁？我怎么没见过你？"},{label = 403,val = "2,0,0,我是<color='#66ff00'>「鱼」</color>~，<color='#66ff00'>「鹰」</color>临时有事，所以我先过来，他等下就来，货呢？"},{label = 401,val = "2,神秘人,40062,哦，原来您就是那位神秘的<color='#66ff00'>「鱼」</color>大人！刚才来消息说，货马上要进入飞瀑村了，可恶，<color='#66ff00'>「鹰」</color>这家伙竟然就让你一个人来！"},{label = 401,val = "2,神秘人,40062,不对，<color='#66ff00'>「鹰」</color>没说会让你来，咱们对下暗号。"},{label = 403,val = "2,0,0,落日？"},{label = 401,val = "2,神秘人,40062,错，暗号是北域，你这个奸细！！！"}}}]],
	[10660]=[[{10660,{{label = 403,val = "2,0,0,北域，货没出事吧？"},{label = 401,val = "2,送货人,40064,星落，没出事，怎么就你一个人来接应？"},{label = 403,val = "2,0,0,那看来我找对人了。"}}}]],
	[10664]=[[{10664,{{label = 405,val = "10194,40260,送货人, {point, 10007, 783, 985}, 2, dead, 1, []},"},{label = 403,val = "2,0,0,现在让我看看你们运的是什么货。"},{label = 213,val = "pick,1000"},{label = 203,val = "16051, 783, 985"},{label = 203,val = "15093, 783, 985"},{label = 403,val = "2,0,0,这……这竟然是，传说中的星辰石<color='#66ff00'>「耀阳」</color>，得赶紧带回去给肖恩教授看看。"},{label = 408,val = "10194"}}}]],
	[10670]=[[{10670,{{label = 401,val = "2,肖恩教授,20019,<color='#66ff00'>[role]</color>，就在你刚送回<color='#66ff00'>「耀阳」</color>之后，我们的盟友送来一个消息，在挖掘出<color='#66ff00'>「耀阳」</color>后，<color='#66ff00'>摘星者</color>并没有撤离。"},{label = 403,val = "2,0,0,还没撤离？难道，极北之域还有一块星辰石？"},{label = 401,val = "2,肖恩教授,20019,很有可能！所以，我需要你去极北之域探查一下，最好叫上<color='#66ff00'>伊芙</color>，她的占星术对寻找星辰石有很大帮助。"},{label = 403,val = "2,0,0,明白了，我去叫上伊芙。"}}}]],
	[10680]=[[{10680,{{label = 405,val = "10280,40000,伊芙, {point, 10001, 336, 2200}, 0,stand, 1, []"},{label = 403,val = "2,0,0,伊芙，最近有空么？"},{label = 401,val = "2,伊芙,40000,当然，最近没什么事呢。"},{label = 403,val = "2,0,0,伊芙，那跟我去一趟极北之域吧，我得到消息说那边还藏有一块星辰石，需要你的帮助。"},{label = 401,val = "2,伊芙,40000,没问题！O(∩_∩)O~"}}}]],
	[10690]=[[{10690,{{label = 405,val = "10285,40000,伊芙, {point, 10007, 748, 1037}, 2,stand, 1, []"},{label = 401,val = "2,大酋长贝恩,20043,谢谢你们，陌生人，这些该死的妖精！竟然发了疯似得攻击我。"},{label = 403,val = "2,0,0,咦，你……你跟凯恩大叔长得好像！"},{label = 401,val = "2,大酋长贝恩,20043,凯恩大叔？那是我的父亲，牛头人部落前任大酋长。"},{label = 403,val = "2,0,0,噢，原来你就是凯恩大叔的儿子贝恩，咦，你受伤了？"},{label = 401,val = "2,大酋长贝恩,20043,好像是的，呵，我，我感觉到体内的力量……在流失……"},{label = 401,val = "2,伊芙,40000,不好，他伤势太重，快去找迷迭香！"},{label = 408,val = "10285"}}}]],
	[10700]=[[{10700,{{label = 405,val = "10200,40000,伊芙, {point, 10007, 783, 985}, 2, stand, 1, []},"},{label = 401,val = "2,伊芙,40000,我记得凯恩大叔说过，牛头人部族生活在这里南部的草原上，你怎么会到这里来？"},{label = 401,val = "2,大酋长贝恩,20043,我最近听说了大雪山的妖精们发疯的事，而这里再往北就是我们部族的圣地，所以想上来看看情况。"},{label = 403,val = "2,0,0,可是，按照你现在的情况，虽然喝下药剂，但也没恢复到能够再战斗的地步啊。"},{label = 408,val = "10200"}}}]],
	[10710]=[[{10710,{{label = 405,val = "10220,40000,伊芙, {point, 10007,1385,2919}, 4, stand, 1, []},"},{label = 203,val = "16051,1391, 2725"},{label = 405,val = "10210,40296,先祖之魂, {point, 10007, 1391, 2725}, 1, dead, 1, []},"},{label = 401,val = "2,先祖之魂,40296,来自异族的孩子，你为何要打扰我的安眠？"},{label = 403,val = "2,0,0,我们带来了您的血裔贝恩的祈求，请您指引我们的方向，我们想知道极北之域发生了什么事，圣地是否会受到影响？"},{label = 401,val = "2,先祖之魂,40296,好吧，孩子，让我看看……不，不，怎么会这样？一群拥有着魔域气息的黑暗怪物占据了圣地。"},{label = 401,val = "2,先祖之魂,40296,黑暗怪物在极北之域的水源上下了毒药，让无数生灵发狂并自相残杀，而他们的灵魂正在痛嚎。"},{label = 401,val = "2,伊芙,40000,黑暗怪物？难道是「摘星者」？我知道了！他们下毒是为了混淆局面，让外界的注意力放在发疯的生灵们身上！从而可以悄悄挖掘星辰石！"},{label = 408,val = "10220"},{label = 408,val = "10210"}}}]],
	[10720]=[[{10720,{{label = 405,val = "10230,40300,嗜血魔熊, {point, 10007,711,307}, 6, stand, 1, []},"},{label = 405,val = "10231,40290,血影魔狐, {point, 10007,757,516}, 5, stand, 1, []},"},{label = 405,val = "10232,40280,黑暗妖精, {point, 10007,846,287}, 0, stand, 1, []},"},{label = 405,val = "10233,40300,嗜血魔熊, {point, 10007,1119,255}, 1, stand, 1, []},"},{label = 405,val = "10234,40067,熔岩领主, {point, 10007,623,426}, 6, stand, 1, []},"},{label = 401,val = "2,熔岩领主,40067,入侵者？找死，都给我上，碾碎他们！"},{label = 403,val = "2,0,0,不好，快走！"},{label = 417,val = "1163:355,1745:720,1308:988"},{label = 408,val = "10230"},{label = 408,val = "10231"},{label = 408,val = "10232"},{label = 408,val = "10233"},{label = 408,val = "10234"}}}]],
	[10730]=[[{10730,{{label = 401,val = "2,大酋长贝恩,20043,诚意？我想起来了，丽丽所在的冰雪妖精一族与迷梦树界的邪梦妖精一族是世仇，从上古起就相互征伐。"},{label = 403,val = "2,0,0,那邪梦妖精的情报是否足以代表结盟的诚意呢。"},{label = 401,val = "2,大酋长贝恩,20043,没错，最好是关于邪梦妖精的首领的情报，邪梦女皇藏得很深，只有她最亲近额女仆们知道她的下落。"}}}]],
	[10740]=[[{10740,{{label = 408,val = "20270"},{label = 405,val = "10240,40310,女皇侍女, {point, 50011,401,1689}, 6, stand, 1, []},"},{label = 405,val = "10230,40000,伊芙, {point, 50011,563,1525}, 1, stand, 1, []},"},{label = 401,val = "2,伊芙,40000,说吧，你们的女皇在哪！"},{label = 401,val = "2,女皇侍女,40310,啊呸！"},{label = 403,val = "2,0,0,不要这样，我不太喜欢拷问别人，不过若是不肯合作的话，我会让你死三天，没错，这不是病句！"},{label = 401,val = "2,女皇侍女,40310,死三天……我，我说，女皇最近移驾到迷梦树界二层的东北部了。"},{label = 408,val = "10230"},{label = 408,val = "10240"}}}]],
	[10750]=[[{10750,{{label = 401,val = "2,大酋长贝恩,20043,我相信我们的前盟友不会背叛我们的，所以，我需要他们的鲜血，交给贤者福波斯，或许贤者会从中找到一些秘密。"}}}]],
	[10760]=[[{10760,{{label = 401,val = "2,佛格瑞特,40340,我……我这是怎么了？"},{label = 403,val = "2,0,0,你醒了就好，之前……被毁灭军团的渗透组织「摘星者」用秘法控制住了。"},{label = 401,val = "2,佛格瑞特,40340,毁灭军团，摘星者！我总有一天要将你们焚成灰烬！"}}}]],
	[10770]=[[{10770,{{label = 405,val = "10250,40340,佛格瑞特,{point,10007,268,1066},7,stand,1,[]"},{label = 401,val = "2,佛格瑞特,40340,[role]，我的小朋友，您终于来了，非常感谢您将我从魔爪中拯救出来，但恳请你再助我一臂之力。"},{label = 403,val = "2,0,0,您请说。"},{label = 401,val = "2,佛格瑞特,40340,苏醒后的这段日子我无时不为曾经的行为感到羞愧，也希望能够用「摘星者」者的血液来清洗我的罪孽，然而孤木难撑，我需要召集我的部下，不过他们仍然在「摘星者」的控制当中。"},{label = 403,val = "2,0,0,没问题，我会帮你将他们解救出来。"},{label = 408,val = "10250"}}}]],
	[10780]=[[{10780,{{label = 405,val = "10260,40340,佛格瑞特,{point,10007,1068,297},6,stand,1,[]"},{label = 405,val = "10262,40000,伊芙,{point,10007,1214,386},6,stand,1,[]"},{label = 502,val = "2"},{label = 401,val = "2,佛格瑞特,40340,[role]，多谢相助，我这就派他们出去侦查「摘星者」的踪迹。"},{label = 403,val = "2,0,0,「摘星者」把你们推出来，自身却隐藏得很深，侦查时需要多加细心啊。"},{label = 405,val = "10261,20043,贝恩,{point,10007,1568,297},2,stand,1,[]"},{label = 401,val = "2,贝恩,20043,或许你们应该往南边去找找，之前的行动我们也算是踏遍了每一寸土地，并没有发现「摘星者」的踪影。"},{label = 502,val = "6"},{label = 215,val = "0,10262,6"},{label = 403,val = "2,0,0,好，我去南边问问，利贝尔王国的王子殿下就在南方驻守，我与他熟识，希望能从他那得到一些有用的信息。"},{label = 401,val = "2,伊芙,40000,那我就去问问妖精公主丽丽殿下，不知道妖精们是否知道「摘星者」的消息。"},{label = 408,val = "10260"},{label = 408,val = "10262"},{label = 408,val = "10261"}}}]],
	[10790]=[[{10790,{{label = 403,val = "2,0,0,王子离开去寻找精灵公主了？真是遗憾，我还想问下殿下是否有关于极北之域「摘星者」踪迹的情报呢。"},{label = 401,val = "2,阿瑞斯,20057,「摘星者」？就在前不久我才看到一群陌生人往北边去了，不知道是不是……"},{label = 104,val = "1,1000"},{label = 401,val = "2,丽丽,20044,不，伊芙！！！！"},{label = 403,val = "2,0,0,不好！伊芙出事了！"}}}]],
	[10800]=[[{10800,{{label = 403,val = "2,0,0,伊芙人呢？出什么事了？"},{label = 401,val = "2,丽丽,20044,对不起，伊芙离开时被一群神秘人掳走了，实在太快了，我没来得及，对不起，对不起……"},{label = 403,val = "2,0,0,神秘人？什么人竟敢劫走伊芙！！！"},{label = 401,val = "2,丽丽,20044,咦？那闪亮亮的是什么？难道是神秘人没注意掉下来的？"}}}]],
	[10810]=[[{10810,{{label = 401,val = "2,肖恩教授,20019,这……这不就是「摘星者」首领阴影领主近卫的饰品么！"},{label = 403,val = "2,0,0,这么说，掳走伊芙的人就是阴影领主的近卫？<color='#66ff00'>（奇怪，肖恩教授竟然认识阴影领主近卫身上的饰品！）</color>"},{label = 401,val = "2,肖恩教授,20019,八九不离十！前短时间商人古德刚来圣心城时，他的魔仆叮叮当到处宣扬曾在极北之域看见阴影领主的踪影，我以为是信口开河没有在意……"},{label = 403,val = "2,0,0,我这就去问问叮叮当！"}}}]],
	[10820]=[[{10820,{{label = 401,val = "2,叮叮当,20050,救命，救命啊，这里要杀人……额，杀魔仆啦！"},{label = 401,val = "2,阴影杀手,40370,别白费力气了，在你到处宣扬我主的行踪时，你就应该料到你会有这一天，受死吧！"},{label = 403,val = "2,0,0,住手！"}}}]],
	[10830]=[[{10830,{{label = 403,val = "2,0,0,好了好了，没事了，叮叮当，告诉我你在哪看到的阴影领主，放心，只要我除掉他，你就不用担心再有杀手来杀你了。"},{label = 401,val = "2,叮叮当,20050,真、真可怕，他们就在极北之域最北方那一代，你快去将他们全都除掉，一个也不要留！"},{label = 403,val = "2,0,0,竟然躲在被我们放弃掉的北方，果真是最危险的地方就是最安全的地方呐！"}}}]],
	[10840]=[[{10840,{{label = 401,val = "2,贝恩,20043,放心吧，[role]，封锁的事情就交给我们，我会联系丽丽和阿瑞斯一起将极北之域的中部和南部都封锁，一只苍蝇都别想飞过！"}}}]],
	[10850]=[[{10850,{{label = 403,val = "2,0,0,哈，我看到你们了！"},{label = 405,val = "10270,40048,阴影领主,{point,10007,941,405},2,stand,1,[]"},{label = 405,val = "10271,40005,扭曲传送门,{point,10007, 695,424},6,stand,1,[]"},{label = 101,val = "700,1060,342"},{label = 401,val = "2,阴影领主,40048,看来我们又有新的客人来了，很抱歉你来晚了，伊芙已经被传送走了，再见！"},{label = 406,val = "10270,1000,695,424"},{label = 408,val = "10270"},{label = 403,val = "2,0,0,伊芙……不……"},{label = 408,val = "10271"},{label = 100,val = "500"}}}]],
	[10860]=[[{10860,{{label = 401,val = "2,古德老爷,20049,好了，现在记住我说的话！{25}{25}{25}"},{label = 401,val = "2,古德老爷,20049,元素变形药剂混合目标的血液能让你在短时间内变成目标的模样，如果你将<color='#66ff00'>被俘的近卫</color>的血液混合药剂后服下，你将变成阴影近卫的模样。"},{label = 401,val = "2,古德老爷,20049,去找<color='#66ff00'>飞瀑村</color>的隐者，喝下药剂后他会帮助你混入阴影近卫的队伍，作为你救下叮叮当的回报，我只能帮你到这了。"}}}]],
	[10870]=[[{10870,{{label = 213,val = "attack,800"},{label = 202,val = "0,0,15021"}}}]],
	[10880]=[[{10880,{{label = 401,val = "2,隐者,20052,很好，你现在看起来跟一个阴影近卫并没有什么区别！"},{label = 403,val = "2,0,0,先生，现在让我们打入阴影近卫内部吧！{25}{25}"},{label = 401,val = "2,隐者,20052,年轻人，不要急，我们得先知道阴影近卫的下落，最近我得知有黑市商人要将一个预言星盘卖给阴影近卫，你去拦下黑市商人，设法从他口中得知来交易的阴影近卫的下落。"}}}]],
	[10890]=[[{10890,{{label = 405,val = "10300,40420,黑市商人,{point,10003,1742, 369},0,stand,1,[]"},{label = 401,val = "2,黑市商人,40420,别打了，别打了，我把星盘送你还不行嘛"},{label = 403,val = "2,0,0,说，你跟阴影近卫准备在哪交易！"},{label = 401,val = "2,黑市商人,40420,就在前面的瀑布上面，有个洞口的地方。"},{label = 408,val = "10300"}}}]],
	[10900]=[[{10900,{{label = 405,val = "10310,40430,近卫兵长,{point,10003, 2980, 559},2,stand,1,[]"},{label = 401,val = "2,近卫兵长,40430,不，不要杀我！"},{label = 403,val = "2,0,0,你想把预言星盘带到哪去？"},{label = 401,val = "2,近卫兵长,40430,我说，别杀我，统领让我带到我们的秘密基地去，就在精灵之森的世界树下，过了云桥就是。"},{label = 213,val = "attack,800"},{label = 214,val = "0,10310,dead,800"},{label = 403,val = "2,0,0,对不起，我不会放过任何一个来自魔域毁灭军团的人，而且，我也没说不杀你！"},{label = 408,val = "10310"}}}]],
	[10910]=[[{10910,{{label = 403,val = "2,0,0,好，我这就去！"},{label = 401,val = "2,精灵长老,20027,等等，带上这个吐真剂，它能让你的俘虏说出实话！"}}}]],
	[10920]=[[{10920,{{label = 405,val = "10320,40450,近卫兵长,{point,10004,1905, 456},0,stand,1,[]"},{label = 213,val = "attack,800"},{label = 203,val = "16078,1905,456"},{label = 403,val = "2,0,0,说，你们要把伊芙带到哪去？"},{label = 401,val = "2,近卫兵长,40450,头……好晕……我不……知道……首领说会在路上留下……<color='#66ff00'>阴影印记</color>指引我们，那是……地面上的一团阴影"},{label = 403,val = "2,0,0,看来要根据给断后的近卫留下的<color='#66ff00'>阴影印记</color>去追踪他们了！"},{label = 408,val = "10320"}}}]],
	[10930]=[[{10930,{{label = 403,val = "2,0,0,您说你看到了伊芙？"},{label = 401,val = "2,风之旅人,20025,大概……是吧，我也不是特别清楚，她跟一群陌生人在一起，往西边去了。"},{label = 403,val = "2,0,0,西边……希望姬小姐能看到！谢谢您，疯子女……啊，风之旅人"}}}]],
	[10940]=[[{10940,{{label = 405,val = "10330,40001,小红帽,{point,10002,2879, 1477},2,stand,1,[]"},{label = 403,val = "2,0,0,小红帽，好久不见呀，听你主人说你见到了伊芙？知道她往哪去了么？"},{label = 401,val = "2,小红帽,40001,我凭什么告诉你啊？你还追赶过我，哼！除非……我听说那边的<color='#66ff00'>海盗</color>抢回来一些海岛上的水果，好像很好吃的样子。"}}}]],
	[10950]=[[{10950,{{label = 408,val = "10330"},{label = 405,val = "10340,40001,小红帽,{point,10002,1400, 1650},6,stand,1,[]"},{label = 401,val = "2,小红帽,40001,啊，海盗跑了，水果都是我的了！好了，我就大发慈悲告诉你，我看见伊芙跟一群怪人在一起，有个怪人还跟神像旁的<color='#66ff00'>老渔夫</color>说话来着"},{label = 408,val = "10340"}}}]],
	[10960]=[[{10960,{{label = 405,val = "10360,40470,近卫首领,{point,10003,2214, 391},1,stand,1,[]"},{label = 401,val = "2,近卫首领,40470,可恶，你怎么能找到我们？"},{label = 403,val = "2,0,0,靠着很多人的帮助，而且我们副院长肖恩教授也出手了！"},{label = 401,val = "2,近卫首领,40470,肖恩？他？他怎么能……"},{label = 221,val = "1010"},{label = 600,val = "600"},{label = 214,val = "0,10370,1001,800"},{label = 214,val = "0,10360,dead,800"},{label = 401,val = "2,神秘人,40490,不要与魔域的人交流，再见。"},{label = 408,val = "10370"},{label = 403,val = "2,0,0,这人是谁，为什么要打垮近卫首领，真奇怪……对了，伊芙，你没事吧？"},{label = 215,val = "0,10350,2"},{label = 401,val = "2,伊芙,40000,我没事，这次真是多亏你了，他们……哎，我想想回学院休息一下"},{label = 403,val = "2,0,0,好的，那我去向肖恩教授报告好消息。"},{label = 408,val = "10360"}}}]],
	[10970]=[[{10970,{{label = 405,val = "10372,40000,伊芙,{point,10001,480, 3642},2,stand,1,[]"},{label = 403,val = "2,0,0,伊芙，你不好好休息找我来做什么？还这么神神秘秘的，约在人少的地方。"},{label = 401,val = "2,伊芙,40000,就是要约在人少的地方啊，避免被偷听嘛~先别问什么事了，帮我去仓库管理员洋葱那里取一下我放那的东西，我怕自己去会引人注目。"},{label = 403,val = "2,0,0,引人注目？真是奇怪……好吧，我帮你去取"},{label = 408,val = "10372"}}}]],
	[10980]=[[{10980,{{label = 405,val = "10380,40000,伊芙,{point,10001,2377, 3945},4,stand,1,[]"},{label = 403,val = "2,0,0,现在能告诉是什么事了吧，搞得这么神神秘秘的。"},{label = 401,val = "2,伊芙,40000,真是拿你没办法……好吧，我之前不是被阴影近卫绑架了么，他们向利用我占星的能力帮助预测星辰石「暮雪」的地点，但没有预言星盘之类的道具我的准确率会很低，所以他们又去黑市买预言星盘。"},{label = 401,val = "2,伊芙,40000,巧合的是，我偷偷占星了一次，发现「暮雪」就在我脚下，也就是阴影近卫在精灵之森的临时营地里，因为太近所以一下子就占星出来了！"},{label = 403,val = "2,0,0,所以，我们现在是去要挖掘「暮雪」？"},{label = 401,val = "2,伊芙,40000,是的，那个货物是我回来后收集的能够掩藏星辰石气息的道具隐匿斗篷，咱们这就分头出发吧，在风之旅人那里汇合。"},{label = 408,val = "10380"}}}]],
	[10990]=[[{10990,{{label = 405,val = "10390,40000,伊芙,{point,10002,2509, 572},2,stand,1,[]"},{label = 502,val = "6"},{label = 401,val = "2,伊芙,40000,你终于来了，怎么这么慢，害我还担心，以为你出什么事了。"},{label = 403,val = "2,0,0,路上遇到了劫匪，耽搁了一下。"},{label = 401,val = "2,伊芙,40000,竟然有劫匪……还好你没事，现在你先去精灵之森看看阴影近卫以前的临时营地有人没有，我可不想引起别人的注意。"},{label = 408,val = "10390"}}}]],
	[11000]=[[{11000,{{label = 405,val = "10400,40000,伊芙,{point,10002,2509, 572},2,stand,1,[]"},{label = 502,val = "6"},{label = 403,val = "2,0,0,营地被一群伐木工占据了，不过已经被我赶走了，现在就过去么？"},{label = 401,val = "2,伊芙,40000,先别急，时间太短，我怕被其他人发现就在收到隐藏星辰石气息的隐匿斗篷后就过来了，但星辰石隐藏在地下深处，我听说露露能炼制地心钻探机，那个能够帮我们挖掘出「暮雪」。"},{label = 403,val = "2,0,0,我这就去找露露。"},{label = 408,val = "10400"}}}]],
	[11010]=[[{11010,{{label = 403,val = "2,0,0,拿到地心钻探机了！"},{label = 401,val = "2,伊芙,40000,那咱们出发吧！"}}}]],
	[11020]=[[{11020,{{label = 405,val = "10410,40000,伊芙,{point,10004,1946, 513},4,stand,1,[]"},{label = 214,val = "0,10410,1000,1000"},{label = 401,val = "2,伊芙,40000,还好有隐匿斗篷，一点异像都没有！"},{label = 403,val = "2,0,0,搞定，走，回学院交给肖恩教授。"},{label = 401,val = "2,伊芙,40000,这个……不能交给<color='#66ff00'>肖恩教授</color>！"},{label = 403,val = "2,0,0,为什么？？？？"},{label = 401,val = "2,伊芙,40000,我偶然间听见阴影近卫的交谈，他们说，我会的这种占星术并不是普通的占星术，这世上只有「摘星者」的二首领<color='#66ff00'>巨龙先知</color>会，可惜<color='#66ff00'>巨龙先知</color>有事走不开，才抓我去的！"},{label = 401,val = "2,伊芙,40000,但是，你知道么，我这占星术是<color='#66ff00'>肖恩教授</color>教我的！"},{label = 403,val = "2,0,0,这……难道<color='#66ff00'>肖恩教授</color>就是「摘星者」的二首领<color='#66ff00'>巨龙先知</color>？这不可能，他可是我们学院的副院长啊！院长大人失踪几十年，他实际上就是我们的院长！"},{label = 401,val = "2,伊芙,40000,你先别急，我们先悄悄带回去，告诉德林老师，让老师悄悄调查一下<color='#66ff00'>肖恩教授</color>吧！"},{label = 408,val = "10410"}}}]],
	[11030]=[[{11030,{{label = 405,val = "10420,40000,伊芙,{point,10001,455, 2215},6,stand,1,[]"},{label = 403,val = "2,0,0,伊芙，听德林老师说你需要我的帮助？"},{label = 401,val = "2,伊芙,40000,[role]，你终于来了，快去码头帮我看看我订的货到了没有，我走不开。"},{label = 403,val = "2,0,0,好。"},{label = 408,val = "10420"}}}]],
	[11040]=[[{11040,{{label = 405,val = "10430,40000,伊芙,{point,10001,455, 2215},6,stand,1,[]"},{label = 401,val = "2,伊芙,40000,原来货物被海盗劫走了呀，我还在疑惑怎么一直没到呢，还好有你在，[role]~"},{label = 401,val = "2,伊芙,40000,对了，快去把这个洞察水晶安置在教授的花园外，小心别让人发现了。"},{label = 408,val = "10430"}}}]],
	[11050]=[[{11050,{{label = 405,val = "10440,40000,伊芙,{point,10001,735, 2174},6,stand,1,[]"},{label = 401,val = "2,伊芙,40000,希望……教授不会让我们失望……院长失踪的这些年，一直是教授在管理学院，要是……"},{label = 101,val = "1000,2134,1808"},{label = 405,val = "10441,40590,神秘人,{point,10001,2571, 1561},6,stand,1,[]"},{label = 600,val = "500"},{label = 406,val = "10441,1000,1920,1594"},{label = 408,val = "10441"},{label = 401,val = "2,伊芙,40000,咦？从教授房里出来的是谁？怎么从没见过！而且之前也没见有人进如教授房里啊。"},{label = 401,val = "2,导师德林,20001,[role]，你和伊芙一起找借口去看一下教授是否还在房里，试探完就跟上那个神秘人，时间上应该来得及。"},{label = 100,val = "1000"}}}]],
	[11060]=[[{11060,{{label = 405,val = "10450,40000,伊芙,{point,10001,2765, 607},6,stand,1,[]"},{label = 405,val = "10451,40590,神秘人,{point,10001,3590, 632},6,stand,1,[]"},{label = 101,val = "800,3657,630"},{label = 401,val = "2,神秘人,40590,你是？"},{label = 221,val = "1011"},{label = 401,val = "2,神秘人,20019,是我，长话短说，东西我带来了，你一定要安全的带回去，还有告诉你的头，别再让我给他擦屁股！"},{label = 401,val = "2,神秘人,40590,遵命，大人辛苦了。"},{label = 401,val = "2,神秘人,20019,走了！"},{label = 221,val = "1012"},{label = 100,val = "800"},{label = 215,val = "0,10450,1"},{label = 401,val = "2,伊芙,40000,奇怪，为什么会有两个教授？算了，先不管这个，我们先去把那个神秘人拦下，抢下他们交易的东西！"},{label = 408,val = "10450"}}}]],
	[11070]=[[{11070,{{label = 405,val = "10460,40000,伊芙,{point,10001,3993, 647},2,stand,1,[]"},{label = 401,val = "2,伊芙,40000,这箱子的工艺我还从没见过，不知道怎么打开。"},{label = 403,val = "2,0,0,去问问炼金师露露吧，希望他有办法打开这宝箱。"},{label = 408,val = "10460"}}}]],
	[11080]=[[{11080,{{label = 403,val = "2,0,0,长老有办法帮我们打开这宝箱么？"},{label = 401,val = "2,大长老,20027,没有对应的密匙，我虽然能打开，但强硬破解会召唤出强大的宝箱守护者，你确定要这样？"},{label = 403,val = "2,0,0,也没有别的办法不是么？麻烦您了，大长老。"},{label = 401,val = "2,大长老,20027,好吧，那宝箱守护者，就靠你自己了。"}}}]],
	[11090]=[[{11090,{{label = 405,val = "10470,40000,伊芙,{point,10004,1609, 1046},6,stand,1,[]"},{label = 203,val = "16078,1792,1047"},{label = 203,val = "16023, 1792,1047"},{label = 401,val = "2,伊芙,40000,咦，这不是我们之前上交给肖恩教授的预言星盘么？怎么会……"},{label = 403,val = "2,0,0,这下事情大条了……两个教授，然后预言星盘被交易……咱们得赶紧回去告诉德林老师。"},{label = 408,val = "10470"}}}]],
	[11100]=[[{11100,{{label = 401,val = "2,导师德林,20001,上次你跟伊芙夺下了神秘人的预言星盘，我已经让伊芙去追查那个神秘人的来历去了。"},{label = 403,val = "2,0,0,那老师您有任务交给我么？"},{label = 401,val = "2,导师德林,20001,当然，出现两个肖恩教授的事不太好查，我决定换个方向，要说学院中对肖恩教授的来历最清楚的，那肯定是当年将教授引进学院的院长普斯特大贤者。"},{label = 403,val = "2,0,0,院长普斯特大贤者？他不是失踪几十年了吗？"},{label = 401,val = "2,导师德林,20001,是的，院长大人将教授引进学院并授予其副院长后没几年就失踪了，不久前密探回报，最近出现在飞瀑村的爱丽儿正是院长失踪前的侍女，你去找她问问看情况吧，记得带上一瓶<color='#66ff00'>踏梦尘</color>。"}}}]],
	[11110]=[[{11110,{{label = 403,val = "2,0,0,爱丽儿小姐，好久不见了呢，其实我这次来是有事请教，请问您认识普斯特大贤者么？"},{label = 401,val = "2,爱丽儿,20053,原来是为了这个……我就知道，当我出现时，学院的人会找上我。是的，我曾经服侍过普斯特大贤者很多年，直到他突然失踪。"},{label = 403,val = "2,0,0,突然失踪？大贤者离开的时候不是把你也带走了么？难道，你跟他也断了联系？"},{label = 401,val = "2,爱丽儿,20053,是的，离开学院后大贤者先后带我去拜访了贤者福波斯与精灵大长老，然后他说他要去做一件大事，让我找地方隐居起来，等他回来找我。"},{label = 403,val = "2,0,0,然而他并没有回来。"},{label = 401,val = "2,爱丽儿,20053,是的，或许他已经抛弃我了吧，所以不久前我来到这里，设法找到回到深海的办法，你如果还想要其它情报的话，去问问曾经跟他深谈过的两人吧。"}}}]],
	[11120]=[[{11120,{{label = 401,val = "2,福波斯,20014,他告诉我他将去做一件很危险的事，在他回来之前，学院将交给刚任职不久的肖恩教授，并希望在肖恩需要帮助时伸出援手。"},{label = 403,val = "2,0,0,为什么院长会将学院交给刚任职不久的肖恩教授，真是奇怪"},{label = 401,val = "2,福波斯,20014,我也觉得奇怪，不过他让我放心，说肖恩值得信任，最后离开前他告诉我他会去精灵之森找大长老拜托一件事，不知道是不是也让长老帮助肖恩。"},{label = 403,val = "2,0,0,好的，谢谢贤者，我这就去精灵之森。"}}}]],
	[11130]=[[{11130,{{label = 403,val = "2,0,0,长老大人，我只是在为学院搜寻院长失踪的线索，如果是重要的秘密，我绝对不会说出去的，请相信我。"},{label = 401,val = "2,大长老,20027,好吧，既然这样我就告诉你吧，普斯特找到了毁灭军团麾下「摘星者」首领阴影领主的踪影，他将去暗杀阴影领主，而在此之前，请我帮助他在我族的时光水晶里封存了他的一部分记忆。"},{label = 403,val = "2,0,0,封存记忆？这又是为什么？"},{label = 401,val = "2,大长老,20027,我也不知，或许是为了避免暗杀失败被读取记忆吧，你如果想要查看时光水晶中的记忆，就必须打败时光水晶的我族上古英灵才行。"}}}]],
	[11140]=[[{11140,{{label = 401,val = "2,普斯特的记忆,20052,我就知道会有人来读取我封存的记忆，无论你是谁，即便是来自学院的同僚或者后辈，请放弃读取我这份记忆的想法，我的记忆只能由我自己或者我指定的人能读取……"},{label = 403,val = "2,0,0,这……想不到院长还做了保险措施，回去问问大长老吧。"}}}]],
	[11150]=[[{11150,{{label = 403,val = "2,0,0,这条线索……据我所知，阴影领主现在依然存在，并没有死亡或者消失。"},{label = 401,val = "2,大长老,20027,那么，普斯特的暗杀行动……所以他现在要吗已经被杀害，要么就应该被关押在「摘星者」的监狱当中。"},{label = 403,val = "2,0,0,只能去「摘星者」的监狱看看了，或许那些被俘虏的阴影近卫知道监狱在哪。"}}}]],
	[11160]=[[{11160,{{label = 405,val = "10470,40650,阴影近卫俘虏,{point,10001,486, 930},6,stand,1,[]"},{label = 401,val = "2,阴影近卫俘虏,40650,别打了！您……您想要问些什么？我一定知无不言，言无不尽！"},{label = 403,val = "2,0,0,你们「摘星者」的监狱在哪？告诉我！"},{label = 401,val = "2,阴影近卫俘虏,40650,监狱？监狱在一个很隐蔽的地方，只有通过冰霜神殿二层的隐秘入口才能进去。"},{label = 403,val = "2,0,0,很好，冰霜神殿二层，我这就过去看看。"},{label = 408,val = "10470"}}}]],
	[11170]=[[{11170,{{label = 405,val = "10490,40660,监狱长摩尔,{point,20001,876, 592},6,stand,1,[]"},{label = 403,val = "2,0,0,说，星辰学院的院长普斯特大贤者是不是关押在这里？"},{label = 401,val = "2,监狱长摩尔,40660,普斯特？他曾经关押在这里，不过没过多久就受不了拷打自杀了，然后我们就把他扔了出去……"},{label = 403,val = "2,0,0,自杀？这……大贤者会自杀？得赶紧回去报告给德林老师。"}}}]],
	[11172]=[[{11172,{{label = 401,val = "2,导师德林,20001,只要知道他们当年来的航班，就能查到来学院之前的行动，依次查下去，总会查出点什么的。"},{label = 403,val = "2,0,0,交给我吧，老师。"}}}]],
	[11174]=[[{11174,{{label = 401,val = "2,杰森队长,20029,请一定要抓住他们，可不能让他们败坏教授的名声！"},{label = 403,val = "2,0,0,没问题，我这就过去抓住那些可恶的家伙！"}}}]],
	[11176]=[[{11176,{{label = 405,val = "10496,40670,伪教授使者,{point,10001,4665,966},0,stand,1,[]"},{label = 401,val = "2,伪教授使者,40670,别打了，别打了，求放过我这一回。"},{label = 403,val = "2,0,0,以后别打着教授使者的身份招摇撞骗，另外，把你从仓库管理员洋葱那里拿的东西还我！"},{label = 401,val = "2,伪教授使者,40670,好的好的，我一定听您的，这是奉教授之命……哦，不，这是我擅自骗来的乘客记录表。"},{label = 403,val = "2,0,0,滚吧！"},{label = 408,val = "10496"}}}]],
	[11180]=[[{11180,{{label = 403,val = "2,0,0,出什么事了？"},{label = 401,val = "2,丽丽,20044,这事说来也是非常奇怪，当时肖恩教授养伤的地方，曾经生活着一群剑灵。"},{label = 401,val = "2,丽丽,20044,然而自从肖恩教授离开之后，那里的剑灵发生了异变，头上竟然长出了类似巨龙之角一样的双角，同时原本温驯的他们变得极具领地意识，且实力大增。"},{label = 403,val = "2,0,0,这确实有点奇怪，我去设法取得一点他们的血液研究下，或许与肖恩教授有关。"}}}]],
	[11182]=[[{11182,{{label = 403,val = "2,0,0,这些剑灵头上长着龙角，该不会跟巨龙有关系吧，甚至是巨龙血裔。"},{label = 403,val = "2,0,0,算了，收集一点他们的血液，拿回去给德林老师看看吧，希望他能从中分析出什么。"}}}]],
	[11190]=[[{11190,{{label = 405,val = "10500,40000,伊芙,{point,10001,776, 2154},6,stand,1,[]"},{label = 403,val = "2,0,0,能让剑灵产生异变，只能是纯血龙族的血液，亚龙的血液是无法达到这种程度的。"},{label = 401,val = "2,导师德林,20001,是的，这么说来，难道教授其实是一头纯血巨龙？"},{label = 401,val = "2,伊芙,40000,这样一来，教授是「摘星者」二号首领「巨龙先知」的可能性就非常大了，第一，他懂「巨龙先知」的预言魔法，第二，他是巨龙，第三，来历不明，以及上次的预言星盘事件。"},{label = 403,val = "2,0,0,教授嫌疑最大，然而他在林雷与马丁事件，以及后续的星辰石争夺中极力打压「摘星者」，这又是为了什么？"},{label = 401,val = "2,导师德林,20001,当务之急，是要验明教授真实身份，我们需要来自「摘星者」内部高层的情报，杰森一直在审问之前你们俘虏的阴影近卫，你去问问看是否有什么进展吧。"}}}]],
	[11192]=[[{11192,{{label = 405,val = "10502,40730,隐形人,{point,10002,3407, 1568},2,stand,1,[]"},{label = 401,val = "2,隐形人,40730,你……你到底想干嘛！"},{label = 403,val = "2,0,0,我想要从你口中知道一点东西，比如，你们「摘星者」组织某些首领的情报……"},{label = 401,val = "2,隐形人,40730,哼，痴心妄想！！！"},{label = 403,val = "2,0,0,没关系，请继续嘴硬，会有人让你开口的，跟我走吧。"},{label = 408,val = "10502"}}}]],
	[11200]=[[{11200,{{label = 403,val = "2,0,0,是的，我们想从他口中得到关于「摘星者」二号首领「巨龙先知」身份的情报。"},{label = 401,val = "2,杰森队长,20029,这可属于绝密情报啊，我试试看能不能从他嘴中撬出来吧，要是有一瓶露露炼制的吐真剂与折磨药剂一起配合使用就好了。"},{label = 403,val = "2,0,0,交给我吧。"}}}]],
	[11210]=[[{11210,{{label = 405,val = "10510,40730,隐形人,{point,10001,827, 961},3,stand,1,[]"},{label = 502,val = "7"},{label = 213,val = "attack,800"},{label = 202,val = "0,10510,16110"},{label = 401,val = "2,隐形人,40730,啊啊啊啊，好痛，啊啊，痛死我啦！！！！"},{label = 401,val = "2,杰森队长,20029,效果不错，接下来给他服下吐真剂。"},{label = 213,val = "attack,800"},{label = 202,val = "0,10510,10066"},{label = 401,val = "2,隐形人,40730,你对我……做了……啊……主人……您要问我什么？"},{label = 403,val = "2,0,0,你知道你们二号首领「巨龙先知」的真实身份么？如果你不知道的话，谁有可能知道？"},{label = 401,val = "2,隐形人,40730,我……我不知道，领主大人，以及我的上司，组织情报主官「无面」应该知道，我……我本来准备明日与主官在冰霜神殿三层碰头交换情报的。"},{label = 408,val = "10510"}}}]],
	[11212]=[[{11212,{{label = 405,val = "10512,40750,无面,{point,50023,230, 484},7,dead,1,[]"},{label = 403,val = "2,0,0,竟然晕过去了……比他的下属隐形人还不如。"},{label = 403,val = "2,0,0,算了，拖回去让杰森大哥帮忙审问吧。"},{label = 213,val = "attack,800"},{label = 408,val = "10512"}}}]],
	[11220]=[[{11220,{{label = 403,val = "2,0,0,杰森队长，你究竟从他口中得到了什么？"},{label = 401,val = "2,杰森队长,20029,哎……他告诉我，「摘星者」的二号首领「巨龙先知」正是我们学院现在暂代院长之职的副院长肖恩教授！"},{label = 403,val = "2,0,0,这……虽然之前种种迹象让教授产生嫌疑，然而想不到竟然真的是他！不过这样一来，预言星盘以及近卫首领被杀事件也就说得通了。"},{label = 401,val = "2,杰森队长,20029,真是令人难以置信，你快去征求德林的意见吧，现在，除了你和他以及伊芙，我都不知该相信谁了。"}}}]],
	[11230]=[[{11230,{{label = 403,val = "2,0,0,可是教授实力强大，他可是足以与三大贤者比肩的人呐！"},{label = 401,val = "2,导师德林,20001,但是这世上所有人都会有缺点，教授强大的地方应该就是他的纯血巨龙的身份，但这也是他的最大弱点。"},{label = 401,val = "2,导师德林,20001,这世上有一种叫做「龙诞香」的香料，只有足够的量，就能让纯血巨龙陷入沉睡，即便量不够，也足以让巨龙陷入虚弱状态，你去问问情报贩子风之旅人，他应该知道「龙诞香」的情报。"}}}]],
	[11232]=[[{11232,{{label = 405,val = "10514,40770,宝库主官,{point,10004,1351, 2191},0,stand,1,[]"},{label = 403,val = "2,0,0,说吧，你们的财宝都放在哪，我需要借用一下。"},{label = 401,val = "2,宝库主官,40770,钱是组织的，命是自己的，算了，你随便拿吧，只求放过我这一次，我再也不为这破组织效命了！"},{label = 403,val = "2,0,0,算你识相，滚吧！"},{label = 408,val = "10514"}}}]],
	[11240]=[[{11240,{{label = 401,val = "2,妖精芭芘,20026,上古残留至今的冰霜神殿中仍存有部分「龙诞香」，但却是由风暴巫妖一族守护着，那是一群通过魔法仪式转化为风暴元素的强大亡灵。"},{label = 401,val = "2,妖精芭芘,20026,每一个风暴巫妖都非常强大，而他们的统帅风暴主宰实力更是深不可测，他亲自看守「龙诞香」，我上次也是无意间捡到了一点。"},{label = 403,val = "2,0,0,可我需要的不只是一丁点啊。"},{label = 401,val = "2,妖精芭芘,20026,那就只能击败他了，不过她平时一般处于隐身状态，且无法被识破，只能通过碾碎她最厌恶的太阳花才能将他引诱出来。"}}}]],
	[11244]=[[{11244,{{label = 203,val = "16078,2046,630"},{label = 401,val = "2,风暴主宰,40810,是谁这么可恶，竟敢在我的领地撒野？"},{label = 403,val = "2,0,0,抱歉，借用一下「龙诞香」。"},{label = 401,val = "2,风暴主宰,40810,借用？用什么借，你的性命么？哈哈，那就拿来吧。"}}}]],
	[11246]=[[{11246,{{label = 405,val = "10520,40810,风暴主宰,{point,50023,2046, 630},1,stand,1,[]"},{label = 401,val = "2,风暴主宰,40810,转为巫妖数百年，想不到还不如年纪轻轻的小孩子，罢了罢了，「龙诞香」拿去吧……哎"},{label = 403,val = "2,0,0,得罪了，多谢。"},{label = 408,val = "10520"}}}]],
	[11250]=[[{11250,{{label = 401,val = "2,导师德林,20001,所以只能从拆穿他身份这件事上做文章了，但也不能点明他的身份，若点明，则他将更加谨慎，肯定会防着「龙诞香」这一致命弱点。"},{label = 403,val = "2,0,0,也许可以利用预言星盘的事，告诉他我们知道了与他交易的神秘人的身份，这样他便会担心自己是否暴露，但又不敢确定。"},{label = 401,val = "2,导师德林,20001,嗯，对，这个方法不错，这样，你去写一封匿名的密信，记住，密信绝对不能暴露我们的身份且绝对保密，你去找露露，她应该有办法。"}}}]],
	[11252]=[[{11252,{{label = 405,val = "10520,40830,高阶督军,{point,10003,1214, 312},6,dead,1,[]"},{label = 401,val = "2,高阶督军,40830,「摘星者」万岁，你们，终将被……毁灭……"},{label = 408,val = "10520"},{label = 403,val = "2,0,0,终于干掉他了， 将他气血覆上信纸，应该就能让教授相信我们是从内部发现了他的身份。"}}}]],
	[11260]=[[{11260,{{label = 401,val = "2,肖恩教授,20019,[role]，你怎么在这里？咦，这是「龙诞香」的香味，原来如此，看来你已经知道了我的身份。"},{label = 403,val = "2,0,0,教授，想不到你真的是「摘星者」的二号首领「巨龙先知」，你……你辜负了全大陆！"},{label = 401,val = "2,肖恩教授,20019,哈哈哈，我辜负了全大陆？你太年轻了，什么都不懂。好了，也别废话了，既然你知道了我的秘密，就别想着活着离开这里，另外，估计德林与伊芙也知道了吧？解决你之后，我会去拜访他们的。"}}}]],
	[11270]=[[{11270,{{label = 405,val = "10520,40851,肖恩斯塔兹,{point,10001,3288, 524},1,stand,1,[]"},{label = 401,val = "2,肖恩斯塔兹,40851,想不到……你已如此……强大……我……很欣……"},{label = 214,val = "0,10520,dead,1000"},{label = 403,val = "2,0,0,教授……"},{label = 408,val = "10520"}}}]],
	[11280]=[[{11280,{{label = 403,val = "2,0,0,是什么事？放心，如果需要我帮忙的话，只要在我能力范围内，我一定竭尽所能。"},{label = 401,val = "2,安妮,20000,也不是需要你帮忙啦，只是最近有个奇怪的流浪商人向我打听肖恩教授的事，肖恩教授有事外出的消息全城都知道，但他总是问些其它问题旁敲侧击，好像肖恩教授外出有什么问题似的。"},{label = 405,val = "10530,20005,露露,{point,10001,1670,3129},7,stand,1,[]"},{label = 401,val = "2,露露,20005,咦，流浪商人也向你打听肖恩教授的事了么？"},{label = 101,val = "500,1670,3128"},{label = 406,val = "10530,500,1632,2832"},{label = 100,val = "500"},{label = 502,val = "1"},{label = 401,val = "2,安妮,20000,是露露姐，怎么，流浪商人也问你了么？"},{label = 401,val = "2,露露,20005,是的，也是这样的，真是令人奇怪。"},{label = 403,val = "2,0,0,<color='#ffff00'>（为了学院安定，肖恩教授的事并没有公布，现在有人试探，其中必有问题）</color>嗯，这么说来确实有点奇怪，我去问问德林老师的意见吧。"},{label = 408,val = "10530"}}}]],
	[11290]=[[{11290,{{label = 401,val = "2,德林,20001,为了避免学院士气招到过大打击，肖恩的事我们并没有宣布，也只有你我还有伊芙知道，看来是有人察觉了不对，不行，我们不能让这事曝光，毕竟学院领袖是敌人内奸的事太影响士气了。"},{label = 401,val = "2,德林,20001,[role]，这事就拜托你了，去打探一下流浪商人的行踪，最好能跟踪一下他，看看他是否想对学院不利。"},{label = 403,val = "2,0,0,好的，老师，交给我吧！"}}}]],
	[11300]=[[{11300,{{label = 405,val = "10540,40869,流浪商人,{point,50011,1223,535},2,stand,1,[]"},{label = 403,val = "2,0,0,咦，流浪商人在那！他在跟谁会面？"},{label = 101,val = "800,1059,538"},{label = 401,val = "2,信使,40860,大人，您来了，有何吩咐？"},{label = 401,val = "2,流浪商人,40869,这里有一封信，你尽快交到领主殿下手上，我还需要再探查一下星辰学院。"},{label = 401,val = "2,信使,40860,遵命。"},{label = 401,val = "2,流浪商人,40869,我先走了。"},{label = 408,val = "10540"},{label = 100,val = "800"},{label = 403,val = "2,0,0,竟然真的是窥探学院的秘密，我必须去劫下信件看看！！！"}}}]],
	[11310]=[[{11310,{{label = 403,val = "2,0,0,让我看看信上写的什么。「阴影殿下，肖恩外出，所为何事暂不可知，前日学院爆发战斗，有巨龙气息，或与肖恩有关，殿下与肖恩会面一事或可推后」。"},{label = 403,val = "2,0,0,阴影殿下？糟糕，这是「摘星者」，必须马上告诉德林老师。"}}}]],
	[11320]=[[{11320,{{label = 401,val = "2,德林,20001,原来阴影领主准备跟肖恩会面，这可是设下陷阱围捕阴影殿下的良机啊！"},{label = 403,val = "2,0,0,可是信和信使都已经落到我们手里了！"},{label = 401,val = "2,德林,20001,没关系，我们可以伪造一份信件，再洗去信使跟你战斗的记忆即可，我可以伪造信件，但这洗去记忆的秘药，只能去向安妮讨要了。"}}}]],
	[11330]=[[{11330,{{label = 401,val = "2,德林,20001,对了，还有一件事，要想成功骗过阴影领主，还得让那个流浪商人消失掉，否则他再报告一次，我们就白费功夫了。"},{label = 403,val = "2,0,0,可当时流浪商人走得很急，我并没有跟上他。"},{label = 401,val = "2,德林,20001,没事，波芘告诉我流浪商人已经回到了学院，这件事，就交给你吧。"}}}]],
	[11340]=[[{11340,{{label = 203,val = "16078,4835,2536"},{label = 403,val = "2,0,0,我就知道胖商人并不是你的真实面目，哼，「摘星者」的密探，我知道你！"},{label = 401,val = "2,流浪商人,40869,不管你知不知道，能见到我真身，就留你不得了！"},{label = 401,val = "2,流浪商人,40870,现在就让你看看我真正的实力！！！"},{label = 408,val = "10561"}}}]],
	[11350]=[[{11350,{{label = 403,val = "2,0,0,老师请说。"},{label = 401,val = "2,德林,20001,今天突然到我们诱杀阴影领主的计划有个绝大的漏洞，那就是——肖恩已死……一般人又无法变身假扮，因为肖恩身为上古巨龙，蛮荒巨兽的气息只要有心探查还是可以探查出来的！至少阴影领主必然知道这一点！"},{label = 403,val = "2,0,0,据我所知这事确实非常难办，或许，我们去找风之旅人问一下，万一他知道有谁可以假扮肖恩呢。"},{label = 401,val = "2,德林,20001,嗯，你这主意不错，去吧，找风之旅人问问。"}}}]],
	[11360]=[[{11360,{{label = 401,val = "2,王大可,20046,你应该知道，中土世界有许多能移山填海的大能，我担心你过去之后不小心惹到什么人弄得尸骨无存，这就……"},{label = 403,val = "2,0,0,有这么恐怖？我们之前不是已经去过花果山帮助美猴王击退毁灭军团过么，怎么没有遇见大能？"},{label = 401,val = "2,王大可,20046,那是因为美猴王当时与天庭净土都有些矛盾……不说也罢，这样，你与我一战，要是能够胜过我，那我就认可你有进入中土世界的能力。我在北方等你。"}}}]],
	[11370]=[[{11370,{{label = 405,val = "10570,20046,王大可,{point,10003,1824,516},0,stand,1,[]"},{label = 401,val = "2,王大可,20046,嗯，你很不错，年纪轻轻就有如此实力，也罢，我就告诉你吧。通往中土世界的位面通道有两条，一是你以前去水帘洞天的通道，那个在你们回来后就关闭了。"},{label = 401,val = "2,王大可,20046,另一个，则在深蓝之洲第三层当中，穿过那个位面通道进入位面中转站，从中就可前往中土世界。不过这一条有位面守望者守护着，记住，千万不可激怒祂！"},{label = 403,val = "2,0,0,好的，多谢大可兄。"},{label = 408,val = "10570"}}}]],
	[11380]=[[{11380,{{label = 401,val = "2,王大可,20046,位面通道陷入沉睡，自然与其一体的位面守望者也不可避免，只要有时空能量药剂，就能唤醒位面守望者，从而打开位面通道。"},{label = 403,val = "2,0,0,我可以问问我们学院的魔药师，她虽然年轻，但懂的东西挺多的。"}}}]],
	[11390]=[[{11390,{{label = 104,val = "1,1000"},{label = 203,val = "16078,428,464"},{label = 401,val = "2,位面守望者,40890,是谁，胆敢打扰我的美梦！我要将你撕成碎片！！！"},{label = 403,val = "2,0,0,等等，这是误会，我是想要……"},{label = 401,val = "2,位面守望者,40890,不管你想干嘛，将我从美梦中唤醒，就得承受我的怒火！！！"}}}]],
	[11400]=[[{11400,{{label = 405,val = "10575,40890,位面守望者,{point,50003,428,464},6,stand,1,[]"},{label = 401,val = "2,位面守望者,40890,好吧，绯月大陆的生灵，既然你打败了我，那你就拥有唤醒我的资格，说吧，你想要什么？"},{label = 403,val = "2,0,0,我想通过您镇守的位面通道前往位面中转站，从而前往中土世界。"},{label = 401,val = "2,位面守望者,40890,我会满足你的请求，不过我之所以沉睡正是因为位面通道受到来自毁灭军团的破坏，沉睡可以自愈，现在你将我提前唤醒，我也只能尝试修复通道，你需要等待一段不长的时间。"},{label = 403,val = "2,0,0,好的，请您尽快，辛苦了，我先回去跟老师报告一声，若是您修好之后找不到我，可以联系星辰学院的导师德林。"},{label = 408,val = "10575"}}}]],
	[11410]=[[{11410,{{label = 403,val = "2,0,0,老师说您修复位面通道出了问题，不知发生什么事了？"},{label = 401,val = "2,位面守望者,40890,通道破损严重，虽然自愈了不少，但有个关键地方无法修复，需要凤凰灵气才能修复，我没有具有凤凰灵气的宝物。"},{label = 403,val = "2,0,0,凤凰灵气？不知道凤凰复活散行不行，你稍等一下，我去给你你找来。"}}}]],
	[11420]=[[{11420,{{label = 405,val = "10580,40890,位面守望者,{point,50003,428,464},6,stand,1,[]"},{label = 401,val = "2,位面守望者,40890,好了，位面通道已然打开，你去吧。"},{label = 403,val = "2,0,0,多谢。"},{label = 408,val = "10580"}}}]],
	[11430]=[[{11430,{{label = 101,val = "700,632,1089"},{label = 403,val = "2,0,0,这里就是位面中转站？咦，那有个人，去问问他看看情况吧。"},{label = 100,val = "700"}}}]],
	[11440]=[[{11440,{{label = 401,val = "2,崔佛尔,20068,你来这里做什么？不知道这里被毁灭军团的人占据着么？"},{label = 403,val = "2,0,0,我只是想通过这里的位面通道去往东土世界的花果山而已，毁灭军团？「摘星者」的母体组织！他们怎么在这？"},{label = 401,val = "2,崔佛尔,20068,哈哈，真不凑巧，那些毁灭军团的人就是从花果山撤退出来的，而通往花果山的位面通道也被他们关闭了！要想去花果山，你得先干掉那些毁灭军团的杂碎！"}}}]],
	[11446]=[[{11446,{{label = 203,val = "16023,1588,697"}}}]],
	[11447]=[[{11447,{{label = 203,val = "16023,982,483"}}}]],
	[11448]=[[{11448,{{label = 203,val = "16023,526,1678"}}}]],
	[11450]=[[{11450,{{label = 104,val = "1,1000"},{label = 101,val = "400,972,1022"},{label = 203,val = "16042,972,1022"},{label = 401,val = "2,崔佛尔,20068,看，位面通道开启了，快去吧，一切小心。"},{label = 100,val = "400"}}}]],
	[11460]=[[{11460,{{label = 101,val = "900,516,873"},{label = 401,val = "2,愤怒的猴帅,40916,呀！竟然又有绯月大陆的异界人来花果山，小的们，揍一顿绑了给二爷爷送去！"},{label = 403,val = "2,0,0,是我啊，我以前还帮助过你们驱赶毁灭军团的人，不记得了么？"},{label = 401,val = "2,愤怒的猴帅,40916,揍的就是你这家伙！小的们，上！"},{label = 100,val = "800"}}}]],
	[11470]=[[{11470,{{label = 405,val = "10590,40916,猴帅,{point,20003,516,873},0,stand,1,[]"},{label = 215,val = "0,10590,5"},{label = 401,val = "2,愤怒的猴帅,40916,二爷爷，二爷爷，绯月大陆的异界客又来了，还欺负我们，爷爷不在，快给我们做主啊！！！"},{label = 406,val = "10590,500,1053,874"},{label = 408,val = "10590"},{label = 101,val = "800,1557,568"},{label = 401,val = "2,二师兄,40910,啊呔，是谁敢在猴哥不在的时候欺负他的猴子猴孙们？哟，原来是你们这些异界客，快上来受老猪一锤！！！"},{label = 100,val = "800"},{label = 403,val = "2,0,0,这误会……是越来越大了，得先让二师兄冷静下来再问清楚！"}}}]],
	[11480]=[[{11480,{{label = 405,val = "10600,40910,二师兄,{point,20003,1557,568},1,stand,1,[]"},{label = 403,val = "2,0,0,二师兄，你先冷静冷静，我绝对没有恶意，来这里也是为了求助，能告诉我是怎么回事么，怎么看到我就喊打喊杀的？"},{label = 401,val = "2,二师兄,40910,哼，谁知道你是不是真的没恶意，就在刚才，一个自称是你朋友的人来了我们这里，看在你曾经帮我们的份上猴哥好心好意的款待，那人却打伤了猴子猴孙逃了出去。"},{label = 403,val = "2,0,0,我的朋友？是谁？等等，美猴王呢？"},{label = 401,val = "2,二师兄,40910,那人自称是绯月大陆的月光女神，与你熟识！至于猴哥，就在你传送进花果山的同时，他刚好从那位面通道传送去了你们绯月大陆。"},{label = 403,val = "2,0,0,月光女神？我听都没听过！这样，我先回去问问我老师，或许他知道是谁，顺便帮美猴王报仇。"},{label = 408,val = "10600"}}}]],
	[11490]=[[{11490,{{label = 405,val = "10610,40919,流浪剑士,{point,50031,384,1227},6,stand,1,[]"},{label = 401,val = "2,流浪剑士,40919,你问月光女神？是的，我见过她，至少是见过符合历史记录中的月光女神形象的人。"},{label = 403,val = "2,0,0,能跟我仔细说说么？"},{label = 401,val = "2,流浪剑士,40919,那是很久很久以前啦，我在这里与怪物的战斗中魔法值消耗殆尽，饮用月光泉时，月光泉洒了一些，便有个像是月光女神的人出现，结果她叹息了一句，说可惜不是神圣月光泉，然后就消失不见了。"},{label = 403,val = "2,0,0,神圣月光泉，明白了，看来得去找找魔药师安妮了。"},{label = 408,val = "10610"}}}]],
	[11500]=[[{11500,{{label = 203,val = "12061,1676,1213"},{label = 600,val = "1700"}}}]],
	[11510]=[[{11510,{{label = 203,val = "15050,503,846"},{label = 203,val = "16070,503,846"},{label = 104,val = "1,1000"},{label = 101,val = "500,424,526"},{label = 203,val = "16078,424,526"},{label = 401,val = "2,月光女神,40920,神圣月光泉？浪费啊浪费，你这人，可真是暴殄天物！"},{label = 403,val = "2,0,0,你就是月光女神？快说，你把美猴王引到哪去了！！！"},{label = 401,val = "2,月光女神,40920,美猴王？你在胡说些什么，我根本不认识什么美猴王！哼，不可理喻，我走了。"},{label = 403,val = "2,0,0,想走？没那么容易！"},{label = 100,val = "500"}}}]],
	[11520]=[[{11520,{{label = 405,val = "10620,40920,月光女神,{point,50031,424,526},0,stand,1,[]"},{label = 401,val = "2,月光女神,40920,你……真是欺人太甚！！！我说了，我根本听都没听过什么美猴王！！！"},{label = 403,val = "2,0,0,这么说……你也没去异位面中土世界的花果山？"},{label = 401,val = "2,月光女神,40920,当然，百年来我一直隐居在此，从未踏出过此地一步。"},{label = 403,val = "2,0,0,难道是有人冒充你？冒充你有什么好处？算了，我信你这一次，方才动手是我对不住，我会找出冒充你的人，还你清白。"},{label = 401,val = "2,月光女神,40920,哼，好走不送！！！"},{label = 408,val = "10620"}}}]],
	[11530]=[[{11530,{{label = 403,val = "2,0,0,可我怎么没在位面通道中与他们撞见？"},{label = 401,val = "2,位面流浪者,20068,哈哈哈，你想太多了，位面通道中时空错乱，根本不可能碰见别的人，即便是一同进去，也不会碰见，所以，你与他们错过情有可原。"},{label = 403,val = "2,0,0,好吧，多谢，我得回绯月大陆查看了，再见。"}}}]],
	[11540]=[[{11540,{{label = 405,val = "10630,40929,假月光女神,{point,50011,744,1630},2,stand,1,[]"},{label = 101,val = "600,607,1620"},{label = 401,val = "2,美猴王,40930,哼哼，俺可算是看出来了，你是故意想引俺老孙来此，说吧，你要作甚？"},{label = 401,val = "2,假月光女神,40929,猴王果然聪明，我我只是想请您为我「摘星者」效力，你与星辰学院有些关系，要是能成为我们内应，那对于我们拿下星辰学院乃至绯月大陆的计划必将发挥极大作用。"},{label = 401,val = "2,美猴王,40930,啊呸！你别哄俺老孙，俺可知道你们「摘星者」正是毁灭军团的下属，上次毁灭军团入侵俺花果山的账俺老孙可还没找你们算呢！"},{label = 401,val = "2,假月光女神,40929,那可由不得你了，猢狲！"},{label = 203,val = "16081,477,1628"},{label = 203,val = "16137,477,1628"},{label = 401,val = "2,美猴王,40930,啊……俺……头疼……啊……"},{label = 403,val = "2,0,0,不好，美猴王出事，我得去帮他！"},{label = 408,val = "10630"}}}]],
	[11550]=[[{11550,{{label = 405,val = "10640,40930,美猴王,{point,50011,477,1628},6,stand,1,[]"},{label = 401,val = "2,美猴王,40930,[role]，承蒙你相助，又救了俺一回，以后但有差遣，老孙决不推辞。"},{label = 403,val = "2,0,0,额……其实，现在我就是有事求助您。「摘星者」首领阴影领主决定与他在我们学院的内奸肖恩教授会面，然而肖恩教授已被我们消灭，如今……"},{label = 401,val = "2,美猴王,40930,想让俺变身为那劳什子肖恩？好说好说，既然是对付毁灭军团，老孙便答应了。"},{label = 403,val = "2,0,0,多谢，那请猴王这便随我去见导师德林吧。"}}}]],
	[11560]=[[{11560,{{label = 403,val = "2,0,0,老师，我把美猴王请来了。"},{label = 406,val = "0,100,1114,2006"},{label = 502,val = "0"},{label = 405,val = "10650,40930,美猴王,{point,10001,1269,2342},3,stand,1,[]"},{label = 406,val = "10650,500,1018,2161"},{label = 401,val = "2,德林,20001,大圣，一切，就拜托您了。"},{label = 401,val = "2,美猴王,40930,好说好说，且让老孙给二位变上一变。"},{label = 221,val = "1013"},{label = 403,val = "2,0,0,咦，人呢？"},{label = 401,val = "2,美猴王,20019,哈哈哈，这呐，这呐~"},{label = 101,val = "1000,2733,1557"},{label = 401,val = "2,美猴王,20019,二位，你们看老孙这变的是像也不像呐？"},{label = 401,val = "2,德林,20001,像像像！太像了，而且与肖恩一样，也具有强大的洪荒巨兽的气息，太完美了。那大圣，接下来，就拜托您了。"},{label = 100,val = "1000"}}}]],
	[11570]=[[{11570,{{label = 401,val = "2,德林,20001,接下来便是安静等待了。"},{label = 101,val = "1000,2733,1557"},{label = 403,val = "2,0,0,咦，果然有人，看房顶！"},{label = 101,val = "500,2719,909"},{label = 405,val = "10660,40940,神秘人,{point,10001,2683,865},7,stand,1,[]"},{label = 401,val = "2,德林,20001,那是……感觉见过，但想不起是谁，不好，他要走了！"},{label = 203,val = "16078,2683,865"},{label = 408,val = "10660"},{label = 401,val = "2,德林,20001,在后山！"},{label = 101,val = "800,3746,940"},{label = 203,val = "16078,3746,940"},{label = 405,val = "10661,40940,神秘人,{point,10001,3746,940},6,stand,1,[]"},{label = 403,val = "2,0,0,我去抓他！！！"},{label = 100,val = "1000"},{label = 408,val = "10661"}}}]],
	[11580]=[[{11580,{{label = 403,val = "2,0,0,看这脚印，像是往北去了。"},{label = 401,val = "2,神秘人,40940,哼，我说是谁，原来是你在追踪我。"},{label = 101,val = "500,3105,516"},{label = 401,val = "2,神秘人,40940,过来吧，让我看看敢追踪我的人到底有多强！"},{label = 100,val = "500"}}}]],
	[11590]=[[{11590,{{label = 403,val = "2,0,0,看他的力量性质，像是「摘星者」的成员，希望杰森队长能帮我从他口中撬出些东西来。"}}}]],
	[11600]=[[{11600,{{label = 405,val = "10670,40940,神秘人,{point,10001,824,961},3,stand,1,[]"},{label = 401,val = "2,杰森队长,20029,看看这个！"},{label = 203,val = "16113,824,961"},{label = 401,val = "2,杰森队长,20029,然后是这个！"},{label = 203,val = "16043,824,961"},{label = 401,val = "2,神秘人,40940,啊啊啊啊啊啊……我说，我说，我是阴影领主麾下影卫的首领血伯爵，也是「摘星者」情报机构的首领，这次是领主将至，我我先来查看是否危险。"},{label = 403,val = "2,0,0,哎呀，不好，看来我这是打草惊蛇了，得去请教下德林老师！！！"},{label = 408,val = "10670"}}}]],
	[11610]=[[{11610,{{label = 401,val = "2,德林,20001,抓了血伯爵，那阴影领主与肖恩会面一事就有了波折，但必须将这事促成，我们只能放了他！放了血伯爵！"},{label = 403,val = "2,0,0,放了血伯爵？这岂不是更奇怪么？"},{label = 401,val = "2,德林,20001,如果是肖恩的手下放了血伯爵，就不奇怪了。"},{label = 403,val = "2,0,0,明白了，我去找美猴王帮忙变身。"}}}]],
	[11620]=[[{11620,{{label = 405,val = "10680,40940,血伯爵,{point,10001,318,1230},6,stand,1,[]"},{label = 403,val = "2,0,0,血伯爵大人，快走！另外，请为我向阴影领主大人带去肖恩大人的问候，希望这次偶然事件不会影响到接下来两位大人的会面。"},{label = 401,val = "2,血伯爵,40940,你是肖恩大人的手下？好，我会向领主大人传达肖恩大人的意思的。"},{label = 408,val = "10680"}}}]],
	[11630]=[[{11630,{{label = 403,val = "2,0,0,密信？难道阴影领主要来了？"},{label = 401,val = "2,美猴王,20019,嘿嘿嘿，是的，你果然聪明，不过不是阴影领主，而是他派来的使者，约我在月痕海岸见面，不管怎样，你先随我一起去吧。"},{label = 403,val = "2,0,0,好。"}}}]],
	[11640]=[[{11640,{{label = 405,val = "10680,40940,肖恩（猴王）,{point,10002,1973,1154},2,stand,1,[]"},{label = 406,val = "0,500,1838,1071"},{label = 502,val = "1"},{label = 401,val = "2,肖恩（猴王）,20019,皎月公爵？怎么是你？阴影领主呢？"},{label = 401,val = "2,皎月公爵,40950,领主大人临时有事，难以亲自前来，还望肖恩大人海涵，之前影卫已获得最后一块星辰石「渊虚之魂」，算上肖恩大人手上的，所有星辰石已然集齐，希望此次我能带回阁下学院中的星辰石。。"},{label = 401,val = "2,肖恩（猴王）,20019,他把最后一块都找到了？哼，我把我的给他，这样以后凯撒陛下带领军团正式进入时，就变成了他一个人的功劳了是吧？"},{label = 401,val = "2,皎月公爵,40950,肖恩大人这是何意，并非是谁功劳大小的问题，关键是只要目前星辰石合一，魔染了这个世界的本源，同时让那些妄图利用星辰石构筑防御结界的绯月人绝望才是重中之重！"},{label = 401,val = "2,肖恩（猴王）,20019,既然谁集齐星辰石不重要，那便让他把他手上的给我即可，哼！或者，让他亲自来谈，[role]，送客！"},{label = 408,val = "10680"}}}]],
	[11650]=[[{11650,{{label = 101,val = "500,1625,2848"},{label = 401,val = "2,间谍头目,40970,哈哈，[role]，不枉我监视你这么久，终于找到点好东西，交上去的话，领主大人肯定会有厚赏，多谢啦{17}{17}{17}"},{label = 403,val = "2,0,0,你是谁？为什么监视我？"},{label = 401,val = "2,间谍头目,40970,我是谁？哈哈，事到如今也不妨告诉你，由于你表现过于突出，我是阴影领主大人派来监视你的，这段时间你一直为手中地图奔波，肯定是好东西，乖乖教出来，我可以给你个痛快！{25}{25}"},{label = 100,val = "500"}}}]],
	[11660]=[[{11660,{{label = 401,val = "2,杰森队长,20029,咦，被隐藏起来这块好眼熟！[role]，你看看，像不像飞瀑村的那片飞瀑。{35}{35}"},{label = 403,val = "2,0,0,这么一说，还真挺像的，飞瀑之上还有个红点，好像标识着什么……"},{label = 401,val = "2,杰森队长,20029,这红点位置不就是道格拉斯的位置么？看来道格拉斯对于这张地图应该知道些什么，你去问问他吧"}}}]],
	[11670]=[[{11670,{{label = 403,val = "2,0,0,等等，这是误会，我并不是入侵者{21}{21}我是受肖恩教授指引来这里的。"},{label = 401,val = "2,龙人督军,20091,肖恩？你是说肖恩斯塔兹？那头抛弃自己职责进入人类世界的前任守护巨龙？好吧，虽然他已经离开，但仍然偶尔会与新任守护巨龙巴尼大人联络，你是来找巴尼大人的么？{24}{24}"},{label = 403,val = "2,0,0,<color='#ffff00'>（我就一张地图，哪知道来这干嘛，不过肖恩有与那个巴尼联络，那与巴尼见面试探一下也不错）</color>是的，请问巴尼大人现在在哪{3}{3}"},{label = 401,val = "2,龙人督军,20091,拜见巴尼大人可没那么容易，听说矮人的<color='#00ff00'>麦酒</color>很好喝，如果你能给我找来一瓶的话，我可以告诉你巴尼大人的位置。{10}{10}"}}}]],
	[11680]=[[{11680,{{label = 403,val = "2,0,0,<color='#ffff00'>（肖恩说会派人来取东西，不知道是什么，算了，不管是什么，拿到手再说）</color>好的，谢谢，我这就去找龙侍女爵。"}}}]],
	[11690]=[[{11690,{{label = 405,val = "10690,40940,肖恩的记忆,{point,10009,598,967},6,stand,1,[]"},{label = 401,val = "2,龙侍女爵,20092,肖恩斯塔兹大人，请检视眼前的凡人是否是您选定来取那些东西的人。"},{label = 403,val = "2,0,0,<color='#ffff00'>（糟糕，要露馅了！必须先下手了！）</color>{13}{13}"},{label = 401,val = "2,肖恩的记忆,40940,不错，这便是我选中的孩子，[role]，看到你我便已经明白发生了什么，记住，一定要好好守护星辰石，至于存储的位置，巴尼那孩子会告诉你的。{26}{26}"},{label = 408,val = "10690"},{label = 403,val = "2,0,0,（<color='#ffff00'>咦？我竟然真的是肖恩选中的人！星辰石要交给我……这……之前他所做的一切都说不通啊</color>{35}）女爵大人，我通过验证了吧？"},{label = 401,val = "2,龙侍女爵,20092,看来你真的是大人选定的那人，你去找巴尼大人吧，我会通知他的。"}}}]],
	[11700]=[[{11700,{{label = 401,val = "2,巴尼,20093,不过星辰石是诸神的遗产，作为诸神忠诚伙伴的守护巨龙一族天然对其拥有所有权，虽然那些星辰石大多数是你发掘并上交给肖恩叔叔的，但你必须向我和我的族人证明你的实力。{25}"},{label = 403,val = "2,0,0,要如何证明？{35}"},{label = 401,val = "2,巴尼,20093,存储在东南的喷泉下，我和我的族人会在那里等你，战胜我，你就可以带走所有星辰石。{25}"}}}]],
	[11710]=[[{11710,{{label = 405,val = "10700,20093,巴尼,{point,10009,2677,2109},3,stand,1,[]"},{label = 401,val = "2,巴尼,20093,[role]，我已经明白为何肖恩叔叔会做出将所有星辰石交予你的决定，你的实力，早已超出我们太多，现在，拿去吧。{24}"},{label = 221,val = "1021"},{label = 403,val = "2,0,0,谢谢，我会好好守护它们的！我发誓！<color='#ffff00'>（这件事太奇怪了，必须赶紧回去问问德林老师的意见！）</color>"},{label = 408,val = "10700"}}}]],
	[11720]=[[{11720,{{label = 401,val = "2,导师德林,20001,真是让我想不通，既然他是「摘星者」的二首领，但为什么却要把星辰石指定让你保管，而且还早早做出安排……[role]，你先休息吧，我会调查这件事。"}}}]],
	[11730]=[[{11730,{{label = 403,val = "2,0,0,好的，德林老师，我这就去月痕海岸寻找风之旅人！"}}}]],
	[11740]=[[{11740,{{label = 401,val = "2,风之旅人,20025,我前几天游历大陆探取情报的时候，无意间看到了皎月公爵在迷梦树界深处与一个神秘人物在密谋着什么{101}"},{label = 403,val = "2,0,0,这个皎月公爵，真是贼心不死{32}"},{label = 401,val = "2,风之旅人,20025,快去寻来剑齿虎幻化果，想必依靠剑齿虎强悍的感知与嗅觉，定能找到蛛丝马迹{25}"},{label = 403,val = "2,0,0,没问题，我现在就去！"}}}]],
	[11750]=[[{11750,{{label = 401,val = "2,风之旅人,20025,我知道了！这就是消失已久的幽冥骑士一族！{102}"},{label = 401,val = "2,风之旅人,20025,幽冥骑士能够依靠天赋能力——相位移动穿梭于两个位面之间，怪不得我们这么久的探查竟然找不到丝毫踪迹！{27}"},{label = 401,val = "2,风之旅人,20025,快趁着这次踪迹还未消散，擒拿他们！万万不可让其再次隐匿了{114}"},{label = 403,val = "2,0,0,交给我吧！"}}}]],
	[11760]=[[{11760,{{label = 405,val = "12800,41114,封印掌控者,{point, 50013,449, 653},7,stand,1,[]"},{label = 403,val = "2,0,0,可恶的「摘星者」军团，让你们知道我的厉害！{114}"},{label = 401,val = "2,幽冥骑士,41114,别打了别打了，我投降我投降{11}"},{label = 401,val = "2,幽冥骑士,41114,我们也是被逼无奈，阴影领主控制了我们族中的老弱病残，逼迫我们帮他探取情报！"},{label = 403,val = "2,0,0,该死的阴影领主，居然对弱者出手，真是不能忍！"},{label = 401,val = "2,幽冥骑士,41114,我们探取到的情报都在这里，还有这些关于阴影领主的信息，希望你能解救我的族人们出来。"},{label = 403,val = "2,0,0,我这就去！<color='#ffff00'>（事情越来越复杂了，阴影领主的爪牙已经遍布大陆，快去找德林导师商量一下）</color>"},{label = 408,val = "12800"}}}]],
	[11770]=[[{11770,{{label = 401,val = "2,风之旅人,20025,太好了，有了这些道具我们就不怕封印掌控者的侵蚀了！"},{label = 401,val = "2,风之旅人,20025,快前往<color='#fff000'>位面中转站</color>解除「摘星者」军团对幽冥一族设下的封印，我们的时间已经不多了{12}"}}}]],
	[11780]=[[{11780,{{label = 104,val = "2,1000"},{label = 405,val = "12860,41121,封印掌控者,{point, 20002, 966,1014},1,stand,1,[]"},{label = 104,val = "2,600"},{label = 401,val = "2,封印掌控者,41121,什么？！封印居然被解除了？幽冥一族要逃出生天了"},{label = 401,val = "2,封印掌控者,41121,真是不可饶恕！敢破坏阴影领主大人的计划，你们真是找死！{12}"},{label = 403,val = "2,0,0,「摘星者」的走狗们，打的就是你！"}}}]],
	[11790]=[[{11790,{{label = 405,val = "12870,41121,封印掌控者,{point, 20002, 966,1014},1,stand,1,[]"},{label = 401,val = "2,封印掌控者,41121,你们……居然……掌握……这样的力量……"},{label = 401,val = "2,封印掌控者,41121,凯撒……大人，我……没法见证……最终……时刻了……呃{19}"},{label = 403,val = "2,0,0,这里的敌人居然是凯撒的直属军团！「摘星者」到底在密谋着什么？！"},{label = 403,val = "2,0,0,看来收集星辰石的任务必须赶快进行了！阴影领主，下次见到必定就是你的死期！"},{label = 408,val = "12870"}}}]],
	[11800]=[[{11800,{{label = 401,val = "2,导师德林,20001,居然是凯撒魔王的直属军团？！看来事情越来越复杂了，必须尽快找到阴影领主的下落，夺取剩余的星辰石！[role]，你先休息吧，我会继续调查这件事，随时待命，一旦发现问题，我会立刻联系你！"}}}]],
	[30120]=[[{30120,{{label = 401,val = "2,洋葱,20024,我这里的仓库，分为<color='#66ff00'>道具仓库</color>与<color='#66ff00'>宠物仓库</color>。顾名思义，<color='#66ff00'>道具仓库</color>可以存放你暂时用不上的道具，而<color='#66ff00'>宠物仓库</color>则可存放你暂时用不上的宠物。"},{label = 401,val = "2,洋葱,20024,放心，我这是学院的公益组织，存放与取出都是<color='#66ff00'>免费</color>的，而你存放的无论道具还是宠物，除了限时道具外，都是永久不变的。"}}}]],
	[30130]=[[{30130,{{label = 401,val = "2,菲菲,20002,你想要了解<color='#66ff00'>宠物打书</color>？好的，当你对宠物的技能不满意时，可使用<color='#66ff00'>宠物技能书</color>让宠物学会新的技能。"},{label = 401,val = "2,菲菲,20002,不过你需要注意一下，打书的新技能可能会代替原有技能，另外，当宠物本身带有4个技能时，新技能将必定代替旧有技能（未进阶的护符技能不算在内）。"},{label = 224,val = "0"}}}]],
