	[20229]=[[{20229,{{label = 401,val = "2,飞瀑巡游者,20013,钱袋丢了？好吧，我巡视时会注意的，其实，你们领悬赏时应该多来飞瀑村转转，最近这里……哎"}}}]],
	[20230]=[[{20230,{{label = 401,val = "2,妖精芭芘,20026,[role]，谢谢你了，等我先化个妆，完了之后就回复你们首席。"}}}]],
	[20231]=[[{20231,{{label = 401,val = "2,精灵大长老,20027,想知道长寿秘诀？我们精灵正常寿命就是几千年，真要说长寿秘诀，那就是下辈子投胎做精灵吧！"}}}]],
	[20250]=[[{20250,{{label = 222,val = "0,0,,给你松松土，花儿快快成长吧，成熟了才有制药材料啊~,3000"}}}]],
