	[20252]=[[{20252,{{label = 222,val = "0,0,,离孵化还有段时间，快出来吧小家伙，外面的世界很精彩。,3000"}}}]],
	[20253]=[[{20253,{{label = 222,val = "0,0,,害虫已经除净了，小苗啊，快快长大吧~我可等着吃了你的果实变身呢~,3000"}}}]],
	[20254]=[[{20254,{{label = 222,val = "0,0,,果然，雪域上已经有太阳花生长了，熔岩巨人的出现对气候影响太大了！,3000"}}}]],
