	[20471]=[[{20471,{{label = 401,val = "2, 首席秘言,20041,终于可以松口气了，被抢走那么多装备，我可没办法把这大窟窿给补上啊。"}}}]],
	[20472]=[[{20472,{{label = 401,val = "2, 首席秘言,20041,幸好有你抢回药品，否则不知多少学院师生会因为缺少药品落下后遗症。"}}}]],
	[20473]=[[{20473,{{label = 222,val = "0,0,,法网恢恢，疏而不漏，就算你们逃掉，我也会抓住你们！,3000"}}}]],
