	[20475]=[[{20475,{{label = 222,val = "0,0,,跑啊，倒是跑啊，害我追这么远，累死我了。,3000"}}}]],
	[20476]=[[{20476,{{label = 222,val = "0,0,,犯我学院天威者，虽远必诛，说得就是你们！,3000"}}}]],
