	[20682]=[[{20682,{{label = 222,val = "0,0,,哼！以后还敢说我们天院的坏话，就揍得你生活不能自理！,3000"}}}]],
	[20683]=[[{20683,{{label = 222,val = "0,0,,别灰心，小野，只有屡败屡战才能真正成长，强者都是由弱者成长而来的！,3000"}}}]],
	[20684]=[[{20684,{{label = 401,val = "2, 首席圣骑,20061,哎呀，这小东西真是可爱死了，你在哪个挂机区域抓的？改天我也去抓一只。"}}}]],
	[20685]=[[{20685,{{label = 401,val = "2, 首席圣骑,20061,这小东西天资不错啊，我得去金币市场淘几件符石给它带上，好好培养！争取早日进阶。"}}}]],
	[30050]=[[{30050,{{label = 401,val = "2, 锻造大师,20010,在装备上镶嵌宝石，可使装备获得额外属性，而且咱们提倡可持续发展，不满意还可拆下来哟！"}}}]],
	[30060]=[[{30060,{{label = 401,val = "2, 炼金师露露,20005,学院新研究出来一门洗练炼金术，可以为装备附加额外属性，有机会的话，一定要试试哦~"}}}]],
	[20690]=[[{20690,{{label = 222,val = "0,0,,小样儿，敢挑衅我们圣骑，也不看看自己长啥样~,3000"}}}]],
	[20691]=[[{20691,{{label = 222,val = "0,0,,以后再敢挑衅我们圣骑，见你一次打你一次！,3000"}}}]],
	[20692]=[[{20692,{{label = 222,val = "0,0,,做人要低调，以后少挑衅！这次就先放过你。,3000"}}}]],
	[20693]=[[{20693,{{label = 222,val = "0,0,,以后请安静的做个丑人，不要再犯事，否则……,3000"}}}]],
	[21000]=[[{21000,{{label = 401,val = "2, 管家小暖,20075,主人您好，欢迎回家，我是您的<color='#00ff00'>管家小暖</color>，以后将由我帮助您管理您的家园，请多多关照。"},{label = 401,val = "2, 管家小暖,20075,您可以在我这里<color='#ffff00'>升级房间</color>、<color='#ffff00'>享受房间的功能</color>以及其它，我还可以为您<color='#ffff00'>打扫清洁</color>与<color='#ffff00'>培育星月树</color>哦~"},{label = 401,val = "2, 管家小暖,20075,现在，先让我们来为您的家园<color='#ffff00'>建造</color>各种房间吧~"}}}]],
	[30170]=[[{30170,{{label = 401,val = "2, 铜须,20010,最近学院研究出一种新的<color='#ffff00'>装备提升</color>方式，那就是<color='#ffff00'>镶嵌宝石</color>，能够稳定提升你的实力！！！"},{label = 401,val = "2, 铜须,20010,<color='#ffff00'>30级</color>以上装备皆可镶嵌，我给你一颗宝石，现在让我来教你镶嵌吧，先打开你的装备<color='#ffff00'>宝石镶嵌界面</color>。"}}}]],
	[30180]=[[{30180,{{label = 401,val = "2, 王大可,20046,大陆上有许多魔物与恶徒，他们为非作歹无恶不作，他们在巢穴深处往往会建立一座<color='#ffff00'>迷城</color>，用以保护与存储他们掠夺而来的<color='#ffff00'>宝藏</color>。"},{label = 401,val = "2, 王大可,20046,宝藏的<color='#ffff00'>钥匙</color>被分散在迷城当中，你若是能够从他们身上获得<color='#ffff00'>秘宝锤</color>，就能在<color='#ffff00'>迷城</color>中搜寻<color='#ffff00'>宝藏钥匙</color>，以及<color='#ffff00'>宝藏</color>的位置。"}}}]],
	[30190]=[[{30190,{{label = 403,val = "2,0,0,请长老为我详细介绍下精灵一族的宝物。"},{label = 401,val = "2, 精灵长老,20027,精灵宝物就是我们精灵一族的装备，与当今大陆装备不同，我族宝物分为<color='#ffff00'>面具</color>、<color='#ffff00'>指环</color>、<color='#ffff00'>斗篷</color>与<color='#ffff00'>纹章</color>。"},{label = 401,val = "2, 精灵长老,20027,精灵宝物大都成套，若是集齐一套，便能赋予装备者特殊能力，比如装备<color='#ffff00'>4件剑心套宝物</color>，即可<color='#ffff00'>对30%血以下造成额外伤害</color>。"},{label = 401,val = "2, 精灵长老,20027,听说最近在<color='#ffff00'>普通副本、公会副本</color>等地有精灵宝物现世，你可以去试试。"}}}]],
	[30300]=[[{30300,{{label = 401,val = "2,菲菲,20084,宠物符石分为<color='#ffff00'>力量符石</color>，<color='#ffff00'>生命符石</color>，<color='#ffff00'>智力符石</color>和<color='#ffff00'>敏捷符石</color>。4个低级符石可合成为1个更高级的符石"},{label = 401,val = "2, 菲菲,20084,而护符则分为<color='#ffff00'>太阳护符</color>，<color='#ffff00'>月亮护符</color>和<color='#ffff00'>星辰护符</color>。两个同类护符可以合成一个该类护符，并重置技能个数与技能。护符越高级，出现高级技能的概率越大哦"},{label = 401,val = "2, 菲菲,20084,不同宠物的特性有所不同，要根据宠物的特性进行<color='#ffff00'>护符</color>和<color='#ffff00'>符石</color>的选择哦."}}}]],
	[30400]=[[{30400,{{label = 401,val = "2, 铜须,20010,装备精炼将随机为装备<color='#ffff00'>附加属性</color>，同时有几率改变武器外观。"},{label = 401,val = "2, 铜须,20010,装备精炼消耗<color='#ffff00'>虹彩石</color>，<color='#ffff00'>幻影石</color>和<color='#ffff00'>月光石</color>。"},{label = 401,val = "2, 铜须,20010,装备到达<color='#ffff00'>80</color>级可以精炼。精炼装备是每个强者必经之路，让我来手把手教你该怎么做吧。"}}}]],
	[30500]=[[{30500,{{label = 401,val = "2, 姬,20009,魂石是一种提升<color='#ffff00'>守护成长</color>的方法，它可以使你的守护伙伴变得更强。"},{label = 401,val = "2, 姬,20009,激活魂石功能需要<color='#ffff00'>守护魂石</color>，同时要用<color='#ffff00'>英勇魂石</color>给守护充能。"},{label = 401,val = "2, 姬,20009,玩家达到<color='#ffff00'>45</color>级可以开启魂石功能。好了，快去试试吧。"}}}]],
	[30600]=[[{30600,{{label = 401,val = "2, 南瓜君,20045,新的萌系表情包分为内涵丰富的<color='#ffff00'>小表情</color>和形象生动的<color='#ffff00'>萌宠大表情</color>两部分。"},{label = 401,val = "2, 南瓜君,20045,每3个<color='#ffff00'>表情包子</color>可制造出一个<color='#ffff00'>表情宝箱</color>，开启后将随机获得一个全新的<color='#ffff00'>小表情</color>.当开出的为已拥有表情时，该表情会转换为1个<color='#ffff00'>萌小包</color>。"},{label = 401,val = "2, 南瓜君,20045,每3个<color='#ffff00'>萌小包</color>都可制造出稀有的<color='#ffff00'>天使飞包</color>，使用后将有一定几率获得<color='#ffff00'>萌宠大表情</color>和其它极品道具。"},{label = 401,val = "2, 南瓜君,20045,好了，我就介绍到这里，快去收集萌萌的表情包吧"}}}]],
	[30700]=[[{30700,{{label = 401,val = "2, 爱丽儿,20053,哦？你也听说神秘的<color='#ffff00'>坐骑</color>降临星辰大陆了吗？{117}"},{label = 401,val = "2, 爱丽儿,20053,<color='#ffff00'>坐骑</color>是畅游星辰大陆的好伙伴，不仅如此，她还能提供<color='#ffff00'>额外的属性加成</color>{117}"},{label = 401,val = "2, 爱丽儿,20053,这只<color='#ffff00'>小兔子</color>是我刚孵化出来的坐骑，你先骑上试试吧！"}}}]],
	[30701]=[[{30701,{{label = 401,val = "2, 爱丽儿,20053,等到<color='#ffff00'>78级</color>你就可以正式训练坐骑畅游星辰啦{117}"},{label = 401,val = "2, 爱丽儿,20053,<color='#ffff00'>在此之前</color>，还有其他各式各样获得<color='#ffff00'>独特坐骑</color>的方式，遇到了可不要错过呀{7}"},{label = 401,val = "2, 爱丽儿,20053,快骑着<color='#ffff00'>小兔子</color>去星辰大陆各地转一转吧！记得<color='#ffff00'>三小时</color>后还给我呀{118}"}}}]],
	[41730]=[[{41730,{{label = 403,val = "2,0,0,当然，首先需要知道如何才能<color='#66ff00'>怀孕</color>{24}"},{label = 401,val = "2,保姆小兰,71150,这个简单，<color='#66ff00'>夫妻组队</color>在我这里领取<color='#66ff00'>孕育任务</color>，每天一次，完成后就有几率<color='#66ff00'>怀孕</color>啦~"},{label = 401,val = "2,保姆小兰,71150,<color='#66ff00'>未婚</color>的话，可在我这里购买<color='#66ff00'>天地灵种</color>，种下后能生长为可怀孕的<color='#66ff00'>灵花</color>，每日完成<color='#66ff00'>浇灌灵花</color>的任务即可有几率使灵花<color='#66ff00'>怀孕</color>了{38}"},{label = 403,val = "2,0,0,谢谢，对了，我还想知道怀孕后如何让孩子<color='#66ff00'>出生</color>？"},{label = 401,val = "2,保姆小兰,71150,怀孕后，无论是未婚还是已婚，皆可在我这接取<color='#66ff00'>孕育任务</color>，完成可获得<color='#66ff00'>孕育值</color>，达到<color='#66ff00'>1000</color>即可让孩子诞生啦~另外其它<color='#66ff00'>日常活动</color>也可获得<color='#66ff00'>孕育值</color>哦{3}"}}}]],
	[43000]=[[{43000,{{label = 401,val = "2,精灵大长老,20027,[role]，正好有事拜托你，我们精灵一族的公主蔷薇前段时间突然失踪，整个精灵森林遍寻不得其踪迹。"},{label = 401,val = "2,精灵大长老,20027,恐怕公主已离开精灵之森，我们精灵一族很少履足大陆，想拜托你在大陆上寻找公主，如果可以的话，可以去飞瀑村找公主闺蜜莉娜小姐问问。"},{label = 403,val = "2,0,0,没问题，交给我吧~"}}}]],
	[43010]=[[{43010,{{label = 401,val = "2,莉娜,20036,你说蔷薇失踪了？这……她并没有来我这里啊！她很少离开精灵之森，在外面除了我也不认识……不对，还有一人！"},{label = 401,val = "2,莉娜,20036,她……可能去找利贝尔了，那是她的恋人，可精灵一族是不允许与外族通婚的，前段时间通信时她曾告诉我她想去找利贝尔，所以……你快去极北之域，利贝尔在那边驻守！"}}}]],
	[43020]=[[{43020,{{label = 405,val = "43020,43000,利贝尔王子,{point,10007,380,2645},7,stand,1,[]"},{label = 401,val = "2,利贝尔王子,43000,蔷薇失踪了？没去找莉娜？但是她也没到我这里来啊！该死，就算精灵族不答应我的求婚，也不用用这种借口吧！"},{label = 403,val = "2,0,0,冷静，王子殿下，看大长老的紧张神色，不像是借口。"},{label = 401,val = "2,利贝尔王子,43000,对不起，是我失态了。[role]，我正在这里驻守边境，不能因私废公，你去问问风之旅人，他在精灵之森出口，蔷薇离开他应该看见过。"},{label = 408,val = "43020"}}}]],
	[43030]=[[{43030,{{label = 401,val = "2,风之旅人,20013,精灵族的公主蔷薇殿下？嗯，我有见到她一个人离开精灵之森，看，那边就有她的脚印，顺着脚印应该知道她去哪了吧。"}}}]],
	[43040]=[[{43040,{{label = 405,val = "43040,43000,利贝尔王子,{point,10007,380,2645},7,stand,1,[]"},{label = 401,val = "2,利贝尔王子,43000,[role]，希望你为我带来了好消息。"},{label = 403,val = "2,0,0,抱歉，殿下，我追踪公主留下的痕迹进入了北风森林，但发现那是有人假扮公主以期望诱捕你，而蔷薇殿下的线索，就此断去了。"},{label = 401,val = "2,利贝尔王子,43000,真是可恶……要是让知道谁劫持了蔷薇，我定将他碎尸万段！好吧，[role]，虽然这样了，不过还是希望你以后多多留心，我也会派人去寻找的。"},{label = 401,val = "2,利贝尔王子,43000,另外，希望你能帮我转告精灵族大长老，虽然发生了这样的事，但我不希望我们之间发生战争，毕竟现在大陆最大的敌人应该是来自魔域的毁灭军团！"}}}]],
	[43050]=[[{43050,{{label = 405,val = "43050,40000,伊芙,{point,10001,395, 2143},0,stand,1,[]"},{label = 401,val = "2,利贝尔王子,43000,[role]，琴雅最近有点奇怪，老是闷闷的不肯说话，经常一个人到城外去，看样子有什么心事，你能帮我去开解一下她么？"},{label = 403,val = "2,0,0,好的，琴雅也是我的朋友啊，交给我吧！"}}}]],
	[43060]=[[{43060,{{label = 401,val = "2,琴雅,43002,对不起，[roel]，让你和伊芙担心了呢，我没事，只是……最近有些想念老师。"},{label = 403,val = "2,0,0,老师？德林老师不就在学院里么？"},{label = 401,val = "2,琴雅,43002,不，我说的是进入学院前的老师——巫妖诗人布鲁，是他收养并抚养我长大的，我的音律魔法也是他教的，不过……哎……[role]，能帮我从炎魔那取来一些火焰之精么？"},{label = 403,val = "2,0,0,当然可以。"}}}]],
	[43070]=[[{43070,{{label = 401,val = "2,琴雅,43002,嗯，有了这些火焰之精，加上老师最喜欢的「月沁人」就可以制作烟花了，[role]，能帮我采集一束「月沁人」么？你知道的，我……眼睛看不见。"},{label = 403,val = "2,0,0,原来你要为你老师制作烟花？没问题，我这就去。"}}}]],
	[43080]=[[{43080,{{label = 403,val = "2,0,0,琴雅，你……你能自己制作烟花？"},{label = 401,val = "2,琴雅,43002,恩呢，虽然，眼睛看不见，但当年是老师手把手教我的，有些东西，只要有心，即便看不见也是可以制作的。好了，完成！我去飞瀑村了，谢谢你，[role]。"},{label = 403,val = "2,0,0,哎，等等，我跟你一起去……"}}}]],
	[43090]=[[{43090,{{label = 101,val = "500,2442,298"},{label = 401,val = "2,琴雅,43002,这……一年没来这里竟然就被风暴之灵占据了！"},{label = 100,val = "500"},{label = 403,val = "2,0,0,这里是？"},{label = 401,val = "2,琴雅,43002,因为天生眼盲，我出生后便被抛弃在这里，是老师捡到了我，从那以后，老师每年都带我到这里放烟花，希望……能找到我的亲生父母……"},{label = 403,val = "2,0,0,我这就去把那些风暴之灵赶走！"}}}]],
	[43100]=[[{43100,{{label = 405,val = "43102,43002,琴雅,{point,10003,1763,258},0,stand,1,[]"},{label = 403,val = "2,0,0,琴雅，风暴之灵已经离开了。"},{label = 401,val = "2,琴雅,43002,嗯嗯，谢谢你了，唔，能请你帮我去点燃烟花么？我看不见……以前都是老师做这些。"},{label = 408,val = "43102"}}}]],
	[43110]=[[{43110,{{label = 405,val = "43112,43002,琴雅,{point,10003,2564,459},2,stand,1,[]"},{label = 203,val = "14071,2363,418"},{label = 215,val = "0,43112,0"},{label = 401,val = "2,琴雅,43002,我听见了烟花灿烂的欢鸣，老师，你看见了么？"},{label = 406,val = "43110,500,2542,548"},{label = 215,val = "0,43112,1"},{label = 101,val = "1000,2421,820"},{label = 401,val = "2,琴雅,43002,你说毁灭军团要入侵了，魔域正在接近，为了给我一个安宁的家，你要去刺杀毁灭君王，可是……"},{label = 401,val = "2,琴雅,43002,有你在我身边，我才有家，现在，我连家都没有了……"},{label = 401,val = "2,琴雅,43002,老师，我想你……"},{label = 408,val = "43112"},{label = 100,val = "500"}}}]],
	[43120]=[[{43120,{{label = 405,val = "43122,43011,凯恩,{point,10007,744,971},2,stand,1,[]"},{label = 403,val = "2,0,0,大叔，你们父子这是？"},{label = 401,val = "2,凯恩,43011,抱歉，[role]，吵醒你了，贝恩这是在怨我扔下部落而外出游历，可我这也是为了部落啊，覆巢之下，焉有完卵！眼看着毁灭军团的入侵迫在眉睫，只有找齐七颗星辰石才能防患于未然。"},{label = 401,val = "2,贝恩,20043,要再这样继续下去，恐怕等不到军团入侵，部落就已经不复存在了！雪吼氏族的强盗与小偷已经将部落过冬的存粮偷得一干二净，这个冬天，怕是……父亲，部落需要你的领导！"},{label = 403,val = "2,0,0,雪吼氏族？那个在雪域神出鬼没的强盗与小偷专业户的氏族？或许我能帮上忙！记得福波斯贤者有一具全视水晶。"},{label = 408,val = "43122"}}}]],
	[43130]=[[{43130,{{label = 405,val = "43162,43011,凯恩,{point,10007,744,971},2,stand,1,[]"},{label = 401,val = "2,贝恩,20043,这……能找到雪吼氏族的踪迹？"},{label = 401,val = "2,凯恩,43011,[role]不是一个会说谎的人，我的儿子，仔细看吧。"},{label = 403,val = "2,0,0,它能让我们看到极北之域的任何地点，看，开始了。"},{label = 101,val = "1000,1606,409"},{label = 401,val = "2,贝恩,20043,出来吧，雪吼氏族的小宝贝呢，别藏着躲着了~"},{label = 101,val = "1000,566,429"},{label = 403,val = "2,0,0,唔，北部没有，那我们往南看看。"},{label = 101,val = "1500,1097,1681"},{label = 101,val = "1000,576,2436"},{label = 403,val = "2,0,0,咦，那是什么！"},{label = 405,val = "43613,43020,未知,{point,10007,286,2698},4,stand,1,[]"},{label = 214,val = "0,43163,attack,1000"},{label = 401,val = "2,贝恩,20043,那是雪吼氏族的族长，化成灰我都认识他！"},{label = 100,val = "1500"},{label = 403,val = "2,0,0,好，看来我们找到目标了！"},{label = 408,val = "43162"}}}]],
	[43140]=[[{43140,{{label = 403,val = "2,0,0,好了，雪吼氏族的人已经离开了极北之域，一段时间内你们应该不用担心他们会回来了！"},{label = 401,val = "2,贝恩,20043,[role],真是辛苦你了，部落的事情却需要你来帮忙……哎……要是被偷走的存粮也能夺回就好了。"},{label = 403,val = "2,0,0,啊……忘了这件事了，等着，雪吼氏族的人走的匆忙，我好像看到有粮食留下来。"}}}]],
	[43150]=[[{43150,{{label = 401,val = "2,贝恩,20043,[role]，真是太辛苦你了，不过我有个不情之请，不知道能否答应。"},{label = 403,val = "2,0,0,嗯，你说吧。"},{label = 401,val = "2,贝恩,20043,我的父亲，凯恩，是部落历史上最强大的战士，而他现在却成为你的追随者，我的品格已经得到了我尊敬，不过，我还想试试您的实力到底到了什么地步。希望能与您较量一下，准备好了的话，就来找我吧。"}}}]],
	[43160]=[[{43160,{{label = 405,val = "43191,43011,凯恩,{point,10007,744,971},2,stand,1,[]"},{label = 401,val = "2,贝恩,20043,不愧是能让部落最强大的男人也追随左右的强者，无论是品格还是实力，都让人折服。"},{label = 401,val = "2,凯恩,43011,哈哈，你小子就是自取其辱……怎么样，现在部落的威胁也去掉了，我追随的人的实力和品格都确认了，你，是否可以担负起我离开后的部落？"},{label = 401,val = "2,贝恩,20043,我会竭尽所能，父亲。"},{label = 408,val = "43191"}}}]],
	[43170]=[[{43170,{{label = 401,val = "2,贤者福波斯,20014,[role]，你终于来了，刚好我这里有一个突发情况，需要你帮忙判断一下。"},{label = 403,val = "2,0,0,贤者自己都不能处理，需要我帮忙判断？好吧，您请说。"},{label = 401,val = "2,贤者福波斯,20014,在最近一次驱魔行动中，我们俘虏了三名「摘星者」的成员，你们星辰学院传来密报，这三人当中有一名是学院派遣的间谍，并掌握有「摘星者」在学院当中的间谍的身份。"},{label = 403,val = "2,0,0,「摘星者」在学院当中竟然还有间谍！这种机密的情报应该立刻获取啊，难道……无法判断我方间谍到底是谁？"},{label = 401,val = "2,贤者福波斯,20014,是的……由于与负责与他单线联系的学院院长爱普教授失踪，我们无法知道到底谁是真正的自己人，希望你能帮助我们判断。三名俘虏正处在巡游者的看守之下，你准备好了的话，就去挨个问话吧。"}}}]],
	[43180]=[[{43180,{{label = 403,val = "2,0,0,取名难，告诉我你知道的一切。"},{label = 401,val = "2,取名难,43050,好好好，没问题，我说，我说，我是星辰学院院长爱普教授委派到「摘星者」的间谍，负责搜集情报，这次……"},{label = 403,val = "2,0,0,这些我已经知道了，现在，告诉我你知道的关于另两位的信息。"},{label = 401,val = "2,取名难,43050,唔，我知道的是，<color='#66ff00'>难取名在说谎</color>！"},{label = 403,val = "2,0,0,好吧，我知道了。"}}}]],
	[43190]=[[{43190,{{label = 403,val = "2,0,0,难取名，现在，轮到你了，告诉我你知道的关于另两位的一切。"},{label = 401,val = "2,难取名,43051,我不仅知道另两位的，还知道学院内部「摘星者」的间谍是谁，另外，<color='#66ff00'>名难取肯定在说谎</color>！"},{label = 403,val = "2,0,0,好吧，我会考虑的。"}}}]],
	[43200]=[[{43200,{{label = 401,val = "2,名难取,43052,啊，你来了，快帮我证明我是好人呐~<color='#66ff00'>千万别信取名难和难取名的话，他们都在说谎</color>！"},{label = 403,val = "2,0,0,都在说谎？这……我想一下。"}}}]],
	[43204]=[[{43204,{{label = 401,val = "2,飞瀑村巡游者,20014,怎么样？是否已有头绪？"},{label = 403,val = "2,0,0,我需要再考虑一下……"},{label = 401,val = "2,飞瀑村巡游者,20014,嗯，不过得尽快下决定啊，不能给学院内部的内奸太多时间。考虑好了的话，就去释放你认为的自己人吧，我相信你。"}}}]],
	[43210]=[[{43210,{{label = 401,val = "2,取名难,43053,啊哈，相信我，你做了一个正确的选择！"},{label = 405,val = "43243,20014,福波斯,{point,10003,2306,2134},7,stand,1,[]"},{label = 406,val = "43243,1000,2409,2619"},{label = 215,val = "0,43243,1"},{label = 502,val = "5"},{label = 215,val = "0,43240,5"},{label = 401,val = "2,福波斯,20014,[roel]，星辰学院情报部传来消息，打入「摘星者」内部的我方间谍有个特征，脚底有三颗痣！"},{label = 401,val = "2,取名难,43053,看来瞒不过去了呐。"},{label = 406,val = "43240,1000,2088,2825"},{label = 214,val = "0,43240,1000,1000"},{label = 401,val = "2,难取名,43054,不……"},{label = 214,val = "0,43241,dead,1000"},{label = 401,val = "2,福波斯,20014,[role]……看来是我来晚了，这两人才是真正的「摘星者」，杀了他们，为难取名报仇！"}}}]],
	[43211]=[[{43211,{{label = 405,val = "43243,20014,福波斯,{point,10003,2306,2134},7,stand,1,[]"},{label = 406,val = "43243,1000,2409,2619"},{label = 215,val = "0,43243,0"},{label = 502,val = "4"},{label = 215,val = "0,43242,4"},{label = 401,val = "2,福波斯,20014,[roel]，星辰学院情报部传来消息，打入「摘星者」内部的我方间谍有个特征，脚底有三颗痣！"},{label = 401,val = "2,难取名,43055,我脚底就有三颗痣！"},{label = 401,val = "2,福波斯,20014,那看来[role]你干得不错！除了力量之外还拥有非凡的智慧呐~"},{label = 401,val = "2,难取名,43055,[role]，谢谢你，不过现在快去清除潜伏在学院内部的间谍吧！根据我得到的情报，那是一名无名商人，经常在圣心城城南的商业大街上兜售他的货物。"},{label = 401,val = "2,福波斯,20014,好！那么[role]，现在就开始行动吧！"}}}]],
	[43212]=[[{43212,{{label = 401,val = "2,名难取,43053,啊哈，相信我，你做了一个正确的选择！"},{label = 405,val = "43243,20014,福波斯,{point,10003,2306,2134},7,stand,1,[]"},{label = 406,val = "43243,1000,2409,2619"},{label = 215,val = "0,43243,0"},{label = 502,val = "4"},{label = 215,val = "0,43242,4"},{label = 401,val = "2,福波斯,20014,[roel]，星辰学院情报部传来消息，打入「摘星者」内部的我方间谍有个特征，脚底有三颗痣！"},{label = 401,val = "2,名难取,43055,看来瞒不过去了呐。"},{label = 406,val = "43242,1000,2288,2825"},{label = 214,val = "0,43242,1000,1000"},{label = 401,val = "2,难取名,43054,不……"},{label = 214,val = "0,43241,dead,1000"},{label = 401,val = "2,福波斯,20014,[role]……看来是我来晚了，这两人才是真正的「摘星者」，杀了他们，为难取名报仇！"}}}]],
	[44000]=[[{44000,{{label = 401,val = "2,剑圣,73010,<color='#66ff00'>[role]</color>，多久了？我们停步不前多久了？难带哦，<color='#ffff00'>100级</color>就是我等成长之路的终点？"},{label = 401,val = "2,剑圣,73010,不，提升之路，永无止境，若你还想<color='#ffff00'>突破100级</color>的桎梏，就来<color='#ffff00'>穹顶通道</color>找我吧。"}}}]],
	[44010]=[[{44010,{{label = 401,val = "2,剑圣,73010,你终究还是来了，看来你与我一样也不想停下这前进的脚步，不错，不错。{25}{25}"},{label = 403,val = "2,0,0,关于<color='#ffff00'>突破100级</color>，请问前辈有何教我？"},{label = 401,val = "2,剑圣,73010,自古代<color='#ffff00'>七星</color>陨落化为<color='#ffff00'>星辰石</color>之后，绯月与星界沟通的<color='#ffff00'>星途</color>损毁，世人无法在通过星界吸纳诸界之力<color='#ffff00'>突破百级</color>。"},{label = 401,val = "2,剑圣,73010,不久前，我发现这穹顶通道有<color='#ffff00'>星光碎片</color>降下，若能以之重建<color='#ffff00'>星途</color>，同时能够突破自我，那我等<color='#ffff00'>突破百级</color>指日可待。"},{label = 403,val = "2,0,0,前辈放心，我会努力重建<color='#ffff00'>星途</color>，同时突破自我，争取与前辈一起<color='#ffff00'>突破百级</color>！"}}}]],
	[44500]=[[{44500,{{label = 401,val = "2,导师德林,20001,不错不错，几日没见你居然已经成长了这么多！{25}{25}"},{label = 403,val = "2,0,0,随着自身成长，总感觉脑海中有什么东西想要喷涌而出，请问导师能否解答我的困惑？"},{label = 401,val = "2,导师德林,20001,没错，看来你已经摸到了<color='#ffff00'>职业绝招</color>的边缘！自古代<color='#ffff00'>七星</color>陨落化为<color='#ffff00'>星辰石</color>后，<color='#ffff00'>七大贤者</color>为了拯救世界牺牲自身构建封印，而他们的<color='#ffff00'>绝学</color>也随着封印保留在了<color='#ffff00'>职业晶石</color>中！"},{label = 401,val = "2,导师德林,20001,而遗留下来的这些<color='#ffff00'>职业晶石</color>正是领悟<color='#ffff00'>职业绝招</color>的钥匙，这些现在对于你来说还是有些<color='#ffff00'>为时尚早</color>，等你<color='#ffff00'>达到90级</color>后再来找我吧。"},{label = 403,val = "2,0,0,导师放心，我这就<color='#ffff00'>努力修炼</color>，争取早日达到目标！"}}}]],
	[44510]=[[{44510,{{label = 401,val = "2,导师德林,20001,<color='#ffff00'>职业绝招</color>是上古贤者所流传下来的秘技！自古代<color='#ffff00'>七星</color>陨落化为<color='#ffff00'>星辰石</color>后，<color='#ffff00'>七大贤者</color>为了拯救世界牺牲自身构建封印，而他们的<color='#ffff00'>绝学</color>也随着封印保留在了<color='#ffff00'>职业晶石</color>中！{25}"},{label = 401,val = "2,导师德林,20001,只有找到足够数量的<color='#ffff00'>职业晶石</color>，才能够从中领悟到<color='#ffff00'>职业绝招</color>的真谛！{26}"},{label = 403,val = "2,0,0,不知从何处能够寻得这些失落已久的<color='#ffff00'>职业晶石</color>？{35}"},{label = 401,val = "2,导师德林,20001,<color='#ffff00'>职业晶石</color>散落四地，月痕海岸的<color='#ffff00'>风之旅人</color>游历各地，也许会有一些线索。"},{label = 401,val = "2,导师德林,20001,<color='#ffff00'>风之旅人</color>为人亦正亦邪，不妨带上一些他最喜爱的<color='#ffff00'>蝴蝶花</color>前去询问！{1}"}}}]],
	[44520]=[[{44520,{{label = 401,val = "2,风之旅人,20025,前不久，我在<color='#ffff00'>远古遗迹</color>探险的过程中发现了<color='#ffff00'>七大贤者</color>的踪迹，也发现了一些关于<color='#ffff00'>职业晶石</color>的线索。{101}"},{label = 401,val = "2,风之旅人,20025,但是那里实在太过危险，我并未深入进<color='#ffff00'>遗迹深处</color>，但是从遗迹外围的情况看来，内部一定有<color='#ffff00'>七大贤者</color>封印节点的存在！但是年代太过久远，封印已经有了一定程度的<color='#ffff00'>破损</color>，遗迹的周围布满了强悍的<color='#ffff00'>虚空生物</color>{46}"},{label = 403,val = "2,0,0,还请先生告诉我<color='#ffff00'>遗迹</color>所在的位置！{21}"},{label = 401,val = "2,风之旅人,20025,哼！真是初生牛犊不怕虎，先向我<color='#ffff00'>展现出你的实力</color>吧，要不然，你去送死了不要紧，<color='#ffff00'>德林</color>那家伙可非要找我麻烦不成。{24}"},{label = 403,val = "2,0,0,还请先生多多指教！{47}"}}}]],
	[44530]=[[{44530,{{label = 401,val = "2,风之旅人,20025,不错，<color='#ffff00'>德林</color>教出来了一个好学生。{111}"},{label = 401,val = "2,风之旅人,20025,跟我来吧，位置就在<color='#ffff00'>精灵遗迹</color>的最深处，我们先在遗迹外围探查一番。{101}"}}}]],
	[44531]=[[{44531,{{label = 401,val = "2,风之旅人,20025,这个痕迹，是虚空生物活动的踪迹！按照痕迹深浅来看似乎是刚刚才经过这里{42}"},{label = 401,val = "2,虚空小妖,41129,嗷呜，是生物的气息！{20}"},{label = 401,val = "2,风之旅人,20025,不好，是虚空生物，速度击杀掉它。{114}"}}}]],
	[44535]=[[{44535,{{label = 401,val = "2,风之旅人,20025,看来封印已经破损的很厉害了，快离开这里我们再做准备！{23}"}}}]],
	[44540]=[[{44540,{{label = 401,val = "2,风之旅人,20025,封印的状况比想象中还要糟糕。{28}"},{label = 401,val = "2,风之旅人,20025,想要深入遗迹取得职业晶石就必须加固封印，你且去带来一个爆燃果，我来用它绘制加固封印的符文！{25}"},{label = 403,val = "2,0,0,我这就去！{56}"}}}]],
	[44550]=[[{44550,{{label = 401,val = "2,风之旅人,20025,符文的绘制已经完成了。{25}"},{label = 401,val = "2,风之旅人,20025,事不宜迟，我们速速前往遗迹深处加固封印！寻找职业晶石！{30}"},{label = 403,val = "2,0,0,好的！{1}"}}}]],
	[44560]=[[{44560,{{label = 104,val = "2,1000"},{label = 405,val = "44560,41132,虚空掠夺者,{point, 50033,1598,542},1,stand,1,[]"},{label = 104,val = "2,600"},{label = 401,val = "2,虚空掠夺者,41132,你们是什么人！居然想要封印我们虚空大军进攻这个世界的通道！"},{label = 401,val = "2,虚空掠夺者,41132,真是不可饶恕！就让你们见识下放逐虚空的厉害！{114}"},{label = 403,val = "2,0,0,来自虚空的恶魔，从哪来滚回哪里去吧！{12}"},{label = 408,val = "44560"}}}]],
	[44570]=[[{44570,{{label = 403,val = "2,0,0,呼呼，总算把这个恶魔赶走了{100}"},{label = 401,val = "2,风之旅人,20025,干的不错！你看那个恶魔逃跑的时候落下了什么东西。{115}"},{label = 403,val = "2,0,0,莫非这就是传说中的<color='#ffff00'>职业晶石</color>？！{11}"},{label = 401,val = "2,风之旅人,20025,不错，这就是蕴含了<color='#ffff00'>职业绝招</color>秘密的<color='#ffff00'>职业晶石</color>，此物于我无用，你就拿去吧。{25}"},{label = 403,val = "2,0,0,太好了，这样就可以去找<color='#ffff00'>德林导师</color>复命了！{38}"}}}]],
	[44580]=[[{44580,{{label = 401,val = "2,导师德林,20001,干的真不错！想必获得<color='#ffff00'>职业晶石</color>的过程中经过了不少的波折！{25}{25}"},{label = 401,val = "2,导师德林,20001,想要领悟<color='#ffff00'>职业绝招</color>，单靠一块<color='#ffff00'>职业晶石</color>是无法实现的，只能依靠自身实力的积累！首先，必须准备<color='#ffff00'>10块本职业的晶石</color>，引导其中<color='#ffff00'>灵气</color>进入自己体内，才能突破自身职业的<color='#ffff00'>桎梏</color>！"},{label = 401,val = "2,导师德林,20001,<color='#ffff00'>灵气</color>当然也不是一成不变的，只有手握<color='#ffff00'>晶石</color>，时时感受其中的<color='#ffff00'>灵气</color>补充自身，才能长久不衰！"},{label = 401,val = "2,导师德林,20001,相应的，每次进行<color='#ffff00'>自身历练</color>的时候，都能累积<color='#ffff00'>历练值</color>，来进行<color='#ffff00'>职业绝招的提升</color>！"},{label = 401,val = "2,导师德林,20001,我年轻时探秘各个古迹时也曾得到过一批<color='#ffff00'>职业晶石</color>，我已经把它们放到了<color='#ffff00'>各大首席</color>处，只要你前往<color='#ffff00'>挑战</color>，<color='#ffff00'>胜利时</color>即可获得自己应得的<color='#ffff00'>奖励</color>。"},{label = 403,val = "2,0,0,导师放心，我这就<color='#ffff00'>努力修炼</color>，必不负所望！"}}}]],
	[70000]=[[{70000,{{label = 300,val = "510,0"},{label = 401,val = "2,菲雅,11002,有些人说披甲胄持剑的我没有一点淑女风范，如果他们不能闭上那张臭嘴，那么我会很高兴用我的剑帮助他们闭嘴！"}}}]],
	[70001]=[[{70001,{{label = 300,val = "511,0"},{label = 401,val = "2,琳恩,11007,凭什么因为我头上的角就认为我是异端？不知道“人不可貌相”这句话么？世界语是铁匠铺大师傅教的吗？"}}}]],
	[70002]=[[{70002,{{label = 300,val = "512,0"},{label = 401,val = "2,凯恩,11018,虽然我不再有年轻时那强壮的体魄，但岁月带给我的睿智能让我成为你最优秀的伙伴，请记住，老牛喜欢吃嫩……哦，不，是“姜还是老的辣！”"}}}]],
	[70003]=[[{70003,{{label = 300,val = "513,0"},{label = 401,val = "2,蓝雯,11012,我不是你们口中的女神，也不会做作的称呼自己为女汉子，我就是我，独一无二的我。"}}}]],
	[70004]=[[{70004,{{label = 300,val = "527,0"},{label = 401,val = "2,芬,11004,从现在开始，我会时刻注意你的后背，不只是守护，也是监视，一旦你被黑暗所侵蚀，我的锋刃会第一时间吻上你的咽喉。"}}}]],
	[70005]=[[{70005,{{label = 300,val = "526,0"},{label = 401,val = "2,露露,11010,你必须保证每天给我买糖吃，我才跟你走！哼哼~"}}}]],
	[70006]=[[{70006,{{label = 300,val = "509,0"},{label = 401,val = "2,戴安娜,11001,当看不见我时，不要担心，我正在阴影中守护着你。"}}}]],
