	[70008]=[[{70008,{{label = 300,val = "524,0"},{label = 401,val = "2,魔,11005,来，让本座好好研究研究你的梦境，或许，我们将从中发现一些有趣的东西~"}}}]],
	[70009]=[[{70009,{{label = 300,val = "529,0"},{label = 401,val = "2,无名,11006,用我手中的剑，为您劈开一切荆棘与黑暗，为了绯月！"}}}]],
