	[50020]=[[{50020,{{label = 401,val = "2,见习骑士亚瑟,76526,我听说在<color='#ffff00'>极北之域</color>有人看见强盗们的<color='#ffff00'>脚印</color>，那就拜托你啦，要不是我脚崴了，我就自己去了。"}}}]],
