	[50041]=[[{50041,{{label = 203,val = "30020,688, 1104"},{label = 203,val = "16113,688, 1104"},{label = 401,val = "2,活泼的莉莉,76524,烟花真好看，姐姐肯定也喜欢，走，跟我回家把烟花放给我姐姐<color='#00ff00'>莉娜</color>看吧。"}}}]],
	[50042]=[[{50042,{{label = 203,val = "30020,1100, 1633"},{label = 203,val = "16113,1100, 1633"},{label = 401,val = "2,莉娜,20036,哇，绚烂的烟花，[role]，谢谢你陪莉莉玩，莉莉，也谢谢你为姐姐燃放烟花。不过，现在时间不早了，该回来休息了哦。"},{label = 401,val = "2,活泼的莉莉,76524,嗯，[role]，谢谢你，我跟姐姐休息去啦，希望明天你也能陪我捉迷藏，陪我放烟花~"}}}]],
