local mt = {}
	mt.__index = function(_table, index)
	if DataQuestChain.commitPlotData[index] then return DataQuestChain.commitPlotData[index] end
	local base = loadstring(string.format('return %s',DataQuestChain.commitPlotGetFunc[index]))() if not base then return end
	DataQuestChain.commitPlotData[index]={
		map_base_id=base[1],
		battle_id=base[2],
		npc_id=base[3],
		npc_base_id=base[4],
		find_talk=base[5],
		catch_talk=base[6],
		visit_talk=base[7]
	}
	return DataQuestChain.commitPlotData[index]
end
setmetatable(DataQuestChain.data_commitPlot, mt)